function grd_list_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{    
	if(nCell==16)
	{ //수정 pop-up
		gfn_openPopup("PS1301009B",true,"arg=''");
	}

	var ADATA = ds_close.getColumn(0, "CODE"); 

	//trace("CLO :: "+ADATA);
	if(ADATA == "B"){
		//trace("저장 후 조회");
		fn_query();
	}
}
