function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);

	userGroup = gfn_getUserGroup();

	//trace("userGroup== " + userGroup);

	//cal_datef.value = substr(gfn_getCurrDate(),0,6)+"01";
	cal_datet.value = gfn_getCurrDate();
	//검색 시작일 수정 - 2019.03.30 강민석K
	//cal_datef.value = "20000101"; // 정태성 DR 요청 
	cal_datef.value = gfn_getAfterDate(gfn_getCurrDate(), -60);

	//cal_datet.value = gfn_getLeapLastDay(gfn_getCurrDate());

	//코드콤보 설정 : 사무소,사업구분,업종구분,전공
	var codeArr = ["PS003","PS005","PS006","PS007"];
	var dsObjArr = [ds_zzactss,ds_sagbn,ds_upgbn,ds_major];
	var addTypeArr = [G_CODE_NONE,G_CODE_NONE,G_CODE_NONE,G_CODE_NONE];
	gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);



	ufn_init();
	ufn_initSupportCombo();
	ufn_initLifnrCombo();

	cal_temp.Value = gfn_getCurrDate();

}

/********************************************************************************
* 기      능   : 설치 지사장/팀장들은 결재선만 보이도록
********************************************************************************/
function fn_afterApprAuth()
{

	if(ds_apprAuth.getColumn(0, "DUTY") == '250' || ds_apprAuth.getColumn(0, "DUTY") == '350')
	{
		ds_cond.fireevent = false;
		ds_cond.SetColumn(0, "APPR_AUTH", "Y");
		ds_cond.fireevent = true;
	}
}

