function fn_query() {
	if ( !gfn_checkFromToObj(cal_datef, cal_datet, true, st_date.value, 0, false) ) {
		return;
	}

	//trace("userGroup : " + userGroup);
	ds_cond.SetColumn(0, "USERGROUP", userGroup);//로그인 사용자와 관련된 건만 조회되도록 추가 - 2019.03.29 강민석K

	tit_clearActionInfo();
	tit_addSearchActionInfo("ps15:PS1501001A_S1");
	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true);
}
