}

/********************************************************************************
* 기      능   : 조회 Callback  - 설치협력사 로그인시 협력업체코드 첫번째 선택
********************************************************************************/
function fn_afterLifnr(errCode, errMsg) {
	if( userGroup == '10' )
	{
		ds_lifnr.Filter("CODE=="+quote(G_VEND_CODE_E));
		gfn_insertEmptyRow( ds_upgbn, G_CODE_ALL );
		cb_lifnr.Index = 0;
		cb_type.value = "";
		cb_lifnr.Enable = false;
	}
	else
	{
		ds_lifnr.Filter("SO5==''");
		gfn_insertEmptyRow( ds_lifnr, G_CODE_ALL );
		gfn_insertEmptyRow( ds_zzactss, G_CODE_ALL );
		gfn_insertEmptyRow( ds_upgbn, G_CODE_ALL );
		cb_lifnr.value = "";
		cb_zzactss.value = "";
		cb_type.value = "";
	}
}
