// 달력 처리 부분 
/********************************************************************************
* 기      능   : 그리드에서 날짜 입력시 
********************************************************************************/
function grd_list_OnExpandEdit(obj,nRow,nCell,strVal,nPivotIndex)
{
	//trace("STATE : " + ds_list.GetColumn(nRow, "STATE"));
	if(ds_list.GetColumn(nRow, "STATE") != "B1") //발송
	{
		return false;
	}

	var rect = obj.GetCellRect(nRow, ncell);

	//trace("this.Width : " + this.Width);
	//trace("this.Height : " + this.Height);

	var widths = this.Width - 160;//widths 변수가 없어서 추가 - 2019.03.20 강민석k
	var heights = this.Height - 160;//heights 변수가 없어서 추가 - 2019.03.20 강민석k

	cal_temp.top = rect[1] + 60;
	//trace("t== " + cal_temp.top);
	if (cal_temp.top > heights)
	{
		cal_temp.top = heights;
	}

	cal_temp.Left = rect[0] + 10;
	if (cal_temp.Left > widths)
	{
		cal_temp.Left = widths;
	}

	cal_temp.Visible = true;

	//신청일 컬럼 추가로 nCell index 수정 - 2019.03.25 강민석K
	//if(nCell == 14 || nCell == 17)
	if(nCell == 19)
	{
		cal_temp.value = ds_list.getColumn(nRow, "RECDATE");	
	} 


	cal_temp.SetFocus();

}
/********************************************************************************
* 기      능   : 달력에서 일자 선택했을 경우 
********************************************************************************/
function cal_temp_OnDayClick(obj,strText)
{
	gfn_clearMsgBar();
	//trace("strText== " + strText);
	//trace("grd_list.GetClickedCell()== " + grd_list.GetClickedCell());

	//셀 위치가 바뀌어 수정 - 2019.03.20 강민석K
//	if(grd_list.GetClickedCell() == 14 || grd_list.GetClickedCell() == 17)
	if(grd_list.GetClickedCell() == 19)
	{	
		ds_list.setColumn(ds_list.row, "RECDATE", strText);

		//수령일을 발송일 이전보다 입력하지 못하도록 추가 - 2019.03.25 강민석K
		if(ds_list.GetColumn(ds_list.row, "SENDDATE") > ds_list.GetColumn(ds_list.row, "RECDATE")){
			ds_list.setColumn(ds_list.row, "RECDATE", "");
			gfn_openMsgWin("발송일 이전 일자로 선택할 수 없습니다.", "W", "", "");
		}
	} 

	obj.Visible = false;

}
