/********************************************************************************
* 기      능   : 수령확인자 저장
********************************************************************************/
function fn_save()
{

	if (!ds_list.GetUpdate()) {
		gfn_showAlert("CW00005");
		return;
	}

	//------------------------------------------------------------------------------------------------------
	//수령 처리 시 수령확인자와 수령확인일은 필수로 입력받도록 추가 - 2019.03.25 강민석K
	ds_list_filter.DeleteAll();
	ds_list_filter.Copy(ds_list);
	ds_list_filter.Filter("STATE == 'B1' && DEL_CHK == '1'");

	for(var i=0; i < ds_list_filter.GetRowCount(); i++){
		if(ds_list_filter.GetColumn(i, "RECLASTNAME") != "" && ds_list_filter.GetColumn(i, "RECDATE") == ""){
			gfn_openMsgWin("수령확인일을 입력하세요.", "W", "", "");
			return;
		}
		if(ds_list_filter.GetColumn(i, "RECLASTNAME") == "" && ds_list_filter.GetColumn(i, "RECDATE") != ""){

			gfn_openMsgWin("수령확인자를 입력하세요.", "W", "", "");
			return;
		}
	}


	ds_temp.copyF(ds_list_filter);

	//------------------------------------------------------------------------------------------------------
	if(ds_temp.GetRowCount() == 0)
	{
		gfn_openMsgWin("저장할 내역이 없습니다.");
		return;
	}


	// 검토 처리
	if (!gfn_showConfirm("CI00008","검토처리 하시겠습니까?")) {
		return;
	}



	tit_clearActionInfo();
	tit_addMultiActionInfo("ps15:PS1501001A_U1");
	tit_callService(
          ""
        , ""
        , "ds_list=ds_temp"
        , ""
        , ""
        , "fn_query");




}
/********************************************************************************
* 기      능   : 삭제
* 수      정   : 리스트에 삭제용 체크박스 추가 및 로직 수정 - 2019.03.10 강민석K
********************************************************************************/
function fn_delete() {
	/*
	trace("삭제");
	trace(ds_list.GetCurrow());
	// 검토 처리
	if (ds_list.GetCurrow() == -1) 
	{
		gfn_showAlert("선택된 정보가 없습니다.");
		return;
	}
	if (!gfn_showConfirm("CI00015","현재 선택된 정보를")) {return;}	

	var orderNo = ds_list.GetColumn(ds_list.GetCurrow(), 'ORDERNO');

	ds_cond4.ClearData();
	var i = ds_cond4.AddRow();
	ds_cond4.SetColumn(i, 'DELYN', 'Y');
	ds_cond4.setColumn(i, 'ORDERNO', orderNo);

	trace(121);
	trace(ds_cond4.SaveXML());

	tit_clearActionInfo();
	tit_addMultiActionInfo("ps15:PS1501001A_U2");
	tit_callService(
          ""
        , ""
        , "ds_cond4=ds_cond4"
        , ""
        , ""
        , "fn_query");
	*/

	var checkedCnt = 0;//체크된 항목 수
	var checkedIndex = -1;//삭제할 항목 인덱스

	for(var i = 0; i < ds_list.GetRowCount(); i++){
		if(ds_list.GetColumn(i,"DEL_CHK") == "1"){
			checkedCnt++;
			checkedIndex = i;
		}
	}

	//trace("체크된 항목 수 : " + checkedCnt);

	var auth_level = gfn_getUserGroup();
	//trace("auth_level : " + auth_level);
	//trace("G_USER_ID : " + G_USER_ID);
	//trace("EMPNO : " + ds_list.GetColumn(checkedIndex, "EMPNO"));

	if(checkedCnt == 0){
		gfn_showAlert('CW00046'); 
		return;
	}else if(checkedCnt > 1){
		gfn_showAlert("삭제는 1건씩만 가능합니다.");
		return;
	//삭제 조건 추가 - 2019.03.20 강민석K
	//협력사 직원이 로그인 한 경우 본인이 신청한 건만 삭제 가능
	//본인이 신청한 건이더라도 신청 상태가 아닌 건은 삭제 불가(결재 진행된 건)
	}else if(auth_level == "10" || auth_level == "20"){
		if(G_USER_ID != ds_list.GetColumn(checkedIndex, "EMPNO") || ds_list.GetColumn(checkedIndex, "STATE") != "A0"){
			gfn_openMsgWin("협력사는 본인이 신청한\n[신청상태]의 건만 삭제 가능합니다.", "W", "", "");
			return;
		}
	//삭제 조건 추가 - 2019.03.20 강민석K
	//설치관리팀이 로그인한 경우 모든 신청 건 삭제 가능
	}else if(auth_level != "40"){
		gfn_openMsgWin("삭제 권한이 없습니다.", "W", "", "");
		return;
	}

	//trace("삭제할 항목의 index : " + checkedIndex);
	//trace("삭제할 항목의 프로젝트번호 : " + ds_list.GetColumn(checkedIndex, "PSPID"));
	//trace("삭제할 항목의 ORDERNO : " + ds_list.GetColumn(checkedIndex, "ORDERNO"));

	if(!gfn_showConfirm("CI00015","현재 선택된 정보")){
		return;
	}	

	var orderNo = ds_list.GetColumn(checkedIndex, 'ORDERNO');

	ds_cond4.ClearData();
	var i = ds_cond4.AddRow();
	ds_cond4.SetColumn(i, 'DELYN', 'Y');
	ds_cond4.setColumn(i, 'ORDERNO', orderNo);

	//trace(ds_cond4.SaveXML());

	tit_clearActionInfo();
	tit_addMultiActionInfo("ps15:PS1501001A_U2");
	tit_callService(
          ""
        , ""
        , "ds_cond4=ds_cond4"
        , ""
        , ""
        , "fn_query");
}
