var userGroup;
var stats;
var grup;
//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);

	userGroup = gfn_getUserGroup();

	//trace("G_USER_ID= " + G_USER_ID);
	//trace("VEND_CODE= " + G_VEND_CODE);

	//cal_datef.value = substr(gfn_getCurrDate(),0,6)+"01";
	cal_datet.value = gfn_getCurrDate();
	//검색 시작일 수정 - 2019.03.30 강민석K
	//cal_datef.value = "20000101"; // 정태성 DR 요청 
	cal_datef.value = gfn_getAfterDate(gfn_getCurrDate(), -60);

	//cal_datet.value = gfn_getLeapLastDay(gfn_getCurrDate());

	//코드콤보 설정 : 사무소,사업구분,업종구분,전공
	var codeArr = ["PS003","PS005","PS006","PS007"];
	var dsObjArr = [ds_zzactss,ds_sagbn,ds_upgbn,ds_major];
	var addTypeArr = [G_CODE_NONE,G_CODE_NONE,G_CODE_NONE,G_CODE_NONE];
	gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);

	ufn_initSupportCombo();
	ufn_initLifnrCombo();

	cb_state.index = 0;
}
/********************************************************************************
* 기      능   : 사무소콤보 변경시 설치PM 콤보값 생성
********************************************************************************/
function cb_zzactss_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{

if(userGroup == "40" || userGroup == "30"){
	ds_cond.SetColumn(0,"ZZLIFNR",'');  }

    if(ds_cond.GetColumn(0,"ZZACTSS") == '')
    {
       ds_zzlifnr_cd.UnFilter();
    }
    else
    {
      var arr = ds_cond.GetColumn(0,"ZZACTSS");
	  ds_zzlifnr_cd.Filter("ZZACTSS ==" +quote(arr));
    }


	ds_cond_cd.SetColumn(0,"ZZACTSS",strCode);
	cb_zzpmnum.value = "";

	tit_clearActionInfo();
	tit_addSearchActionInfo("ps02:PS0201001A_S41");
	tit_callService(
		""
		, ""
		, "ds_cond_cd=ds_cond_cd"
		, "ds_zzpmnum_cd=ds_zzpmnum_cd"
		, ""
		, "fn_afterZzactssChange"
		, true,true);
}
/********************************************************************************
* 기      능   : 조회 Callback  - 사무소 변경시
********************************************************************************/
function fn_afterZzactssChange(errCode, errMsg) {
	gfn_insertEmptyRow( ds_zzpmnum_cd, G_CODE_ALL );
	cb_zzpmnum.value = "";

	ds_cond.SetColumn(0,"ZZLIFNR",'');
}

/********************************************************************************
* 기      능   : 지원대상 콤보박스용 조회(ZPST0401)
********************************************************************************/
function ufn_initSupportCombo() 
{
	tit_clearActionInfo();	
	tit_addSearchActionInfo("ps15:PS1501001A_S2");
	tit_callService(
        ""
        , ""
        , ""
        , "ds_supported=ds_supported"
        , ""
        , "ufn_afterInitCombo"
        , true);
}

function ufn_afterInitCombo(errCode, errMsg) {
	ds_supported.insertRow(0);
	ds_supported.setColumn(0, "SUPRTCDT", "- 전체 -");
	cb_supported.value = "";
	cb_supported.Index = 0;
}

/********************************************************************************
* 기      능   : 구매 콤보박스용 조회(ZPST0403) ds_lifnr
********************************************************************************/
function ufn_initLifnrCombo() 
{
	tit_clearActionInfo();	
	tit_addSearchActionInfo("ps15:PS1501001A_S3");
	tit_callService(
        ""
        , ""
        , ""
        , "ds_lifnr=ds_lifnr"
        , ""
        , "ufn_afterInitLifnrCombo"
        , true);
}

function ufn_afterInitLifnrCombo(errCode, errMsg) {
	ds_lifnr.insertRow(0);
	ds_lifnr.setColumn(0, "CODE_NAME", "- 전체 -");
	cb_lifnr.value = "";
	cb_lifnr.Index = 0;


	if(userGroup == "10" || userGroup == "20")
	{
		//var nFindRow = ds_lifnr.SearchRow("CODE=='"+G_VEND_CODE+"'");
		cb_lifnr.Value = G_VEND_CODE;
		cb_lifnr.Enable = false;

	}

}

/********************************************************************************
* 기      능   : 신청
********************************************************************************/
function fn_request(obj) {
	gfn_openPopup("PS1501001B",true,"arg=''");
}

/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) {
	//ds_stats2.Copy(ds_stats);
	ds_zzactss.InsertRow();
	ds_zzactss.SetColumn(0, "CODE", "");
	ds_zzactss.SetColumn(0, "CODE_NAME", "- 전체 -");
	cb_zzactss.index = 0;
}
/********************************************************************************
* 기      능   : form UnloadCompleted 
********************************************************************************/
function fn_OnUnloadCompleted(obj) {
	return gfn_isFormUnload(obj);
}
//=========================================================================================
// [ PART 3 ]
// Main Process Event 정의(조회,입력,삭제,저장,인쇄,form닫기)
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 
********************************************************************************/
function fn_query() {
	if ( !gfn_checkFromToObj(cal_datef, cal_datet, true, st_date.value, 0, false) ) {
		return;
	}

	ds_cond.SetColumn(0, "LIFNRYN", "Y");

	tit_clearActionInfo();
	tit_addSearchActionInfo("ps15:PS1501001A_S1");
	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true);
}
/********************************************************************************
* 기      능   : 닫기  
********************************************************************************/
function fn_close() {
	close();
}

//=========================================================================================
// [ PART 4]
// Callback 함수 정의 : callback 함수명은 메인 함수의 fn_xxxx() 에서 xxxx 부분은 fn_afterXXXX()
// 형태가 되도록 작성 
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	gfn_showMsg("CI00002",ds_list.RowCount()+"");
	for(var i = 0; i<ds_list.rowcount; i++)
	{
		ds_list.SetColumn(i,"PICINSU",tit_getLocalFileName(ds_list.getColumn(i, "PICINSU")));
		ds_list.SetColumn(i,"PICTU",tit_getLocalFileName(ds_list.getColumn(i, "PICTU")));
	}
	ds_list.row = -1;
}
