function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 0 ) {
		// 그리드 표시할 컬럼 선택 
//		gfn_selectAllGrid(obj,nCell,nCell,true);
	} else {
		// 그리드 정렬 처리 
		gfn_sortGrid(obj,nCell);
	}
}
/********************************************************************************
* 기      능   : 결재화면
********************************************************************************/
function grd_list_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if(nCell != 0)
	{

		var arg =  "ORDERNO="  + quote(ds_list.GetColumn(nRow,"ORDERNO"));
		    arg += " CRDAT="   + quote(ds_list.GetColumn(nRow,"CRDAT"));
		    arg += " EMPNO="   + quote(ds_list.GetColumn(nRow,"EMPNO"));
		    arg += " SUPRTCD=" + quote(ds_list.GetColumn(nRow,"SUPRTCD")); 
		    arg += " RECPART=" + quote(ds_list.GetColumn(nRow,"RECPART")); 
		    arg += " RECRANK=" + quote(ds_list.GetColumn(nRow,"RECRANK")); 
		    arg += " RECNAME=" + quote(ds_list.GetColumn(nRow,"RECNAME")); 
		    arg += " RECTEL=" + quote(ds_list.GetColumn(nRow,"RECTEL")); 
		    arg += " RECADDR=" + quote(ds_list.GetColumn(nRow,"RECADDR")); 
		    arg += " TITLE=" + quote(ds_list.GetColumn(nRow,"TITLE")); 
		    arg += " PSPID=" + quote(ds_list.GetColumn(nRow,"PSPID")); 
		    arg += " NOTICE=" + quote(ds_list.GetColumn(nRow,"NOTICE")); 
		    arg += " LIFNR=" + quote(ds_list.GetColumn(nRow,"LIFNR")); 
		    arg += " LIFNR_NM=" + quote(ds_list.GetColumn(nRow,"LIFNR_NM")); 
		    arg += " LIFNREMPNO=" + quote(ds_list.GetColumn(nRow,"LIFNREMPNO")); 
		    arg += " IMPDATE=" + quote(ds_list.GetColumn(nRow,"IMPDATE"));
		    arg += " STATE=" + quote(ds_list.GetColumn(nRow,"STATE"));
		    arg += " SENDDATE=" + quote(ds_list.GetColumn(nRow,"SENDDATE"));



		//trace(arg);    

	    gfn_openPopup("PS1501005A",true, arg); 
	}	
}


