        gfn_selectGridCol("grd_list1");
    } else {
        //gfn_sortGrid(obj,nCell);

    }
}

function cb_temno_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
    if(cb_temno.Text == '- 전체 -')
    {
       ds_zzactss.UnFilter();
       ds_zzlifnr.UnFilter();
       ds_ztemno_cd.UnFilter();
       cb_zzactss.Index = 0;
       cb_lifnr.Index = 0;
       cb_ztemno.Index = 0;
    }
    else
    {
      var arr = ds_cond.GetColumn(0,"DTEXT4");
       ds_zzactss.Filter("LENGTH(CODE) == 0 || DTEXT3 == "+quote(strCode));
       cb_zzactss.Index = 0;
       cb_lifnr.Index = 0;
       cb_ztemno.Index = 0;
    }
}

function div0_grd_list_OnExpandEdit(obj,nRow,nCell,strVal,nPivotIndex)
{
        if( obj.GetCellProp("Body", nCell, "COLID") == "OUTHTM") {
        var nLines      = null; // 기본 값 사용
        var sRegExpr    = null; // 기본 값 사용
        var nMaxLen     = null; // 기본 값 사용
        cf_gd_popupText(obj, nRow, nCell, nLines, sRegExpr, nMaxLen);
    }
}
