function common_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if(cb_temno.Text == '- 전체 -')
    {
       ds_zzactss.UnFilter();
       ds_zzlifnr.UnFilter();
       ds_ztemno_cd.UnFilter();
       cb_zzactss.Index = 0;
       cb_lifnr.Index = 0;
       cb_ztemno.Index = 0;
    }
    else
    {
      var arr = ds_cond.GetColumn(0,"DTEXT4");
	   ds_zzactss.Filter("LENGTH(CODE) == 0 || DTEXT3 == "+quote(strCode));
	   cb_zzactss.Index = 0;
       cb_lifnr.Index = 0;
       cb_ztemno.Index = 0;
    }
}
/********************************************************************************
* 기      능   : 롱텍스트 읽기
********************************************************************************/
function ufn_getLongText(dsObj, tabObj, tabColName, tabId) {
	//alert("요기");
	if ( length( tabObj) == 0 || length( tabColName) == 0 || length( tabId) == 0) {
		return "";
	}

	tit_callService(
        "COMM_ACT"
        , "getLongText2"
        , ""
        , ""
        , "TDID=" + quote(tabId) + " TDNAME=" 
				+ quote(tabColName) +" TDOBJECT=" + quote(tabObj)
        , ""
        , false
        , true
        , true);

    nRow = dsObj.addRow();    
    dsObj.setColumn(nRow, "TDOBJECT", tabObj);
    dsObj.setColumn(nRow, "TDNAME", tabColName);
    dsObj.setColumn(nRow, "TDID", tabId);

    //alert(_LONGTEXT);
    //ufn_replaceSpecialChar(_LONGTEXT);  //문자 변환

    dsObj.setColumn(nRow, "TEXT", _LONGTEXT);

	return _LONGTEXT;

}

function div0_grd_list_OnExpandEdit(obj,nRow,nCell,strVal,nPivotIndex)
{
		if( obj.GetCellProp("Body", nCell, "COLID") == "OUTHTM") {
		var nLines		= null;	// 기본 값 사용
		var sRegExpr	= null; // 기본 값 사용
		var nMaxLen		= null;	// 기본 값 사용
		cf_gd_popupText(obj, nRow, nCell, nLines, sRegExpr, nMaxLen);
	}
}

function div0_grd_list_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{

			// 성적서 유형 (완료)
	if( nCell==9 ) 
	{
	var chk = "C";	
	var suprdt 	= ds_list.GetColumn(nRow, "SUPR_DT"); //접수일자
	var pjt 	= ds_list.GetColumn(nRow, "PSPID"); //접수일자
    var stt     = ds_list.GetColumn(nRow, "STATE");
	var grp     = userGroup;
	var seq     = ds_list.GetColumn(nRow, "SEQNO");  //차수
	var maxseq  = ds_list.GetColumn(nRow, "MAXSEQ");
	var gbn = "N";
	var arg = "PER_CHK="+quote(chk);	
	    arg += " PRE_SUPRDT="+quote(suprdt);  
	    arg += " PRE_PJT="+quote(pjt);   //프롲게트번호
		arg += " PRE_STT="+quote(stt);
		arg += " PRE_GRP="+quote(grp);		
  	    arg += " PRE_SEQ="+quote(seq);	
	    arg += " PRE_GBN="+quote(gbn);
	    arg += " PRE_MAX="+quote(maxseq);
		if ( ds_list.GetColumn(nRow, "STATE") = "B" || ds_list.GetColumn(nRow, "STATE") = "C"){
			gfn_openPopup("PS1204006C", true, arg); 

		}	
	}
}
