function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);

	userGroup = gfn_getUserGroup();

	rd_gbn.Index = 0;
	rd_gbn2.Index = 0;
	cb_zzactss.Index = 0;
	grd_list.Visible = true;
	grd_list1.Visible = false;
	grd_list.top=Shape1.Bottom+1;
	grd_list.left=Shape1.left+1;
    grd_list1.top=Shape1.Bottom+1;
	grd_list1.left=Shape1.left+1;
    var curYear = substr(gfn_getCurrDate(),0,4);
	fn_makeYear(curYear,-5,10);
	cb_monthf.index = parseInt(substr(gfn_getCurrDate(),4,2))-1;
	cb_montht.index = parseInt(substr(gfn_getCurrDate(),4,2))-1;

    ds_cond.SetColumn(0,"USERGROUP",userGroup);

    //코드콤보 설정 : 지역, 구분2
	var codeArr = ["PS003"];
    var dsObjArr = [ds_zzactss];
    var addTypeArr = [G_CODE_ALL];
    gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);

	tit_clearActionInfo();
	tit_addSearchActionInfo("wb01:SEOLCHI_VEND_S2");	

	tit_callService(
		""
		, ""
		, "ds_cond=ds_cond"
		, "ds_zzlifnr=ds_lifnr_cd"  
		, ""
		, "fn_afterQueryC"
		, ""
		, ""
		, true);

	fn_initQuery();
}

function fn_initQuery() {

	tit_clearActionInfo();
	tit_addSearchActionInfo("qm05:QM0501001A_S1");
	tit_addSearchActionInfo("ps02:PS0203001A_S1");
	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_team=ds_team ds_zzlifnr=ds_zzlifnr"
        , ""
        , "fn_afterInitQuery"
        , true);

    tit_clearActionInfo();
    tit_addSearchActionInfo("ps16:PS1601011A_S8");
    tit_callService(
        ""
        , ""
        , "ds_invar=ds_invar"
        , "ds_var=ds_var"
        , ""
        , "fn_afterSearchVar"
        , true);
}



function fn_afterInitQuery(){

	if(userGroup == '10')
	{
		ds_zzlifnr.Filter("CODE=="+quote(G_VEND_CODE_E));
		cb_lifnr.Index = 0;
		cb_lifnr.Enable = false;

		ds_team.Filter("ZZLIFNR=="+quote(G_VEND_CODE_E)+" || CODE==''");
		cb_ztemno.Index = 0;
		cb_ztemno.Enable = true;
	}
	else if(userGroup == '30')
	{
		gfn_insertEmptyRow( ds_zzlifnr, G_CODE_ALL );
		ds_zzlifnr.Filter("ZZACTSS=="+quote(G_AREA_CODE_E)+" || CODE==''");
		cb_lifnr.Index = 0;
		cb_lifnr.Enable = true;
	}
	else
	{
		gfn_insertEmptyRow( ds_zzlifnr, G_CODE_ALL );
		cb_lifnr.Index = 0;
		cb_lifnr.Enable = true;
	}
	gfn_insertEmptyRow( ds_team, G_CODE_ALL );
	cb_ztemno.Index = 0;
}

/********************************************************************************
* 기      능   : 수식변수 조건 셋팅
********************************************************************************/
function fn_afterSearchVar(){

    for(var i=0; i < ds_var.count; i++)
	{
	    if ( ds_var.GetColumn(i,"SCR_ID") == "1601001") 
	    {
	        ds_cond.SetColumn(0,"VAR1", toString(ds_var.GetColumn(i,"VAR1")));  
	    }
	    else if ( ds_var.GetColumn(i,"SCR_ID") == "1601002") 
	    {
	        ds_cond.SetColumn(0,"VAR2", toString(ds_var.GetColumn(i,"VAR1")));   
	        ds_cond.SetColumn(0,"VAR3", toString(ds_var.GetColumn(i,"VAR2")));  
	        ds_cond.SetColumn(0,"VAR4", toString(ds_var.GetColumn(i,"VAR3")));   
	        ds_cond.SetColumn(0,"VAR5", toString(ds_var.GetColumn(i,"VAR4")));   
	    }
	    else if ( ds_var.GetColumn(i,"SCR_ID") == "1601004") 
	    {
	        ds_cond.SetColumn(0,"VAR6", toString(ds_var.GetColumn(i,"VAR1")));   
	        ds_cond.SetColumn(0,"VAR7", toString(ds_var.GetColumn(i,"VAR2")));  
	        ds_cond.SetColumn(0,"VAR8", toString(ds_var.GetColumn(i,"VAR3")));   
	        ds_cond.SetColumn(0,"VAR9", toString(ds_var.GetColumn(i,"VAR4")));   
	    }
	    else if ( ds_var.GetColumn(i,"SCR_ID") == "1601005") 
	    {  
	        ds_cond.SetColumn(0,"VAR10", toString(ds_var.GetColumn(i,"VAR1")));    
	    }
	    else if ( ds_var.GetColumn(i,"SCR_ID") == "1601006")   
	    {
	        ds_cond.SetColumn(0,"VAR11", toString(ds_var.GetColumn(i,"VAR1")));  
	    }
	    else if ( ds_var.GetColumn(i,"SCR_ID") == "1601008") 
	    {
	        ds_cond.SetColumn(0,"VAR12", toString(ds_var.GetColumn(i,"VAR1")));    
	    }
	    else if ( ds_var.GetColumn(i,"SCR_ID") == "1601009") 
	    {
	        ds_cond.SetColumn(0,"VAR13", toString(ds_var.GetColumn(i,"VAR1")));  
	        ds_cond.SetColumn(0,"VAR14", toString(ds_var.GetColumn(i,"VAR2")));    
	    }
     }
}
/********************************************************************************
* 기      능   : 지급예정년 계산(현재년도,당해년도에서 뺄 년수, 당해년도에서 더할 년수
********************************************************************************/
function fn_makeYear(curYear,sNum,eNum)
{
	var rowCnt = eNum-sNum;
	var row = 0;

	for(var i=rowCnt; i>-1; i--)
	{
		ds_yearf_cd.AddRow();
		ds_yearf_cd.SetColumn(row,"CODE",parseInt(curYear)+i+parseInt(sNum));
		ds_yearf_cd.SetColumn(row,"CODE_NAME",(parseInt(curYear)+i+parseInt(sNum))+"년");
		ds_yeart_cd.AddRow();
		ds_yeart_cd.SetColumn(row,"CODE",parseInt(curYear)+i+parseInt(sNum));
		ds_yeart_cd.SetColumn(row,"CODE_NAME",(parseInt(curYear)+i+parseInt(sNum))+"년");
		row++;
	}
	cb_yearf.index = eNum;
	cb_yeart.index = eNum;
}

/********************************************************************************
* 기      능   : 조회 (header 조회)
********************************************************************************/
function fn_query() {
	if(gfn_isNull(cb_yearf, true, st_yymm.value)) return;
	if(gfn_isNull(cb_monthf, true, st_yymm.value)) return;
	if(gfn_isNull(cb_yeart, true, st_yymm.value)) return;
	if(gfn_isNull(cb_montht, true, st_yymm.value)) return;
	if(parseInt(ds_cond.GetColumn(0,"YEARF")+ds_cond.GetColumn(0,"MONTHF")) > 
	   parseInt(ds_cond.GetColumn(0,"YEART")+ds_cond.GetColumn(0,"MONTHT")))
	{
		gfn_showMsg("CW00009");
		cb_yeart.SetFocus();
		return;
	}   

	//설치협력사필드 필수체크(설치협력사 로그인시)
	if( userGroup == '10' && gfn_isNull(cb_lifnr, true, st_lifnr.value)) return;

	var fYear  = parseInt(ds_cond.GetColumn(0,"YEARF"));
	var tYear  = parseInt(ds_cond.GetColumn(0,"YEART"));
	var fMonth = parseInt(ds_cond.GetColumn(0,"MONTHF"));
	var tMonth = parseInt(ds_cond.GetColumn(0,"MONTHT"));
	ds_cond.SetColumn(0,"YYYYMMF",ds_cond.GetColumn(0,"YEARF")+ds_cond.GetColumn(0,"MONTHF"));
	ds_cond.SetColumn(0,"YYYYMMT",ds_cond.GetColumn(0,"YEART")+ds_cond.GetColumn(0,"MONTHT"));
    if(rd_gbn2.Index == 0) {
	    ds_cond.SetColumn(0,"CHK_GBN","10");
		ds_cond.SetColumn(0,"CHK_GBN2","01");
		ds_cond.SetColumn(0,"CHK_GBN3","A");
	} else 	if(rd_gbn2.Index == 1) {
		ds_cond.SetColumn(0,"CHK_GBN","11");
		ds_cond.SetColumn(0,"CHK_GBN2","02");
		ds_cond.SetColumn(0,"CHK_GBN3","A");
	} else 	if(rd_gbn2.Index == 2) {
		ds_cond.SetColumn(0,"CHK_GBN","20");
	    ds_cond.SetColumn(0,"CHK_GBN2","04");
		ds_cond.SetColumn(0,"CHK_GBN3","C");
	}

	if(rd_gbn.Index == 0) {
	    ds_cond.SetColumn(0,"SCR_GUBUN","1");
	} else 	if(rd_gbn.Index == 1) {
	    ds_cond.SetColumn(0,"SCR_GUBUN","2");
	}
	if (Checkbox0.Value == "1"){
	    ds_cond.SetColumn(0,"BANGI","1");
	}
	else if (Checkbox1.Value == "1"){
	    ds_cond.SetColumn(0,"BANGI","2");
	}

    if (Checkbox0.Value == "0" && Checkbox1.Value == "0") {
       	tit_clearActionInfo();    	
		tit_addSearchActionInfo("ps16:PS1601009A_S7");  //==> CsvSaerch로 변경	
		tit_callService(
			""
			, ""
			, "ds_cond=ds_cond"
			, "ds_months=ds_months"
			, ""
			, "fn_afterQuery2"
			, ""
			, ""
			, true);


     } else {
         if(rd_gbn.index == 1) {
			tit_clearActionInfo();
			tit_addSearchActionInfo("ps16:PS1601011A_S6");  //==> CsvSaerch로 변경	

			tit_callService(
				""
				, ""
				, "ds_cond=ds_cond"
				, "ds_list1=ds_list1"
				, ""
				, "fn_afterQuery"
				, ""
				, ""
				, true);
		 } else if(rd_gbn.index == 0){
			tit_clearActionInfo();
			tit_addSearchActionInfo("ps16:PS1601011A_S5");  //==> CsvSaerch로 변경
			tit_callService(
				""
				, ""
				, "ds_cond=ds_cond"
				, "ds_list=ds_list"
				, ""
				, "fn_afterQuery"  
				, ""
				, ""
				, true);
		 }
     }
}
