/********************************************************************************
* 기      능   : 액셀  
********************************************************************************/
function fn_excel() 
{
	if(rd_gbn.index == 1) {
		// Excel Export 황
		if( ds_list1.rowcount > 0 )
		{
		    gfn_exportExcel(grd_list1, "종합평가_평가관리_설치팀장별");
			break;
		}
	} else if(rd_gbn.index == 0) {
	    // Excel Export 
		if( ds_list.rowcount > 0 )
		{
            gfn_exportExcel(grd_list,"종합평가_평가관리_협력사별");
            break;
        }
    }
}

/********************************************************************************
* 기      능   : 사무소 별 협력업체 필터 
********************************************************************************/
function cb_zzactss_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex) 
{
    if(strCode == '') ds_zzlifnr.Filter("1==1");
	else ds_zzlifnr.Filter("ZZACTSS=="+quote(strCode)+" || CODE==''");
	cb_lifnr.Index = 0;
}

/********************************************************************************
* 기      능   : 사무소 별 협력업체 필터 
********************************************************************************/
function rd_gbn_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if(rd_gbn.Index == 1) {
	    grd_list1.Visible = true;
	    grd_list.Visible = false;
	    grd_list.top=grd_list1.top;
	    grd_list.left=grd_list1.left;
	} else if(rd_gbn.Index == 0) {
	    grd_list.Visible = true;
	    grd_list1.Visible = false;
	    grd_list1.top=grd_list.top;
	    grd_list1.left=grd_list.left;
	}
}
/********************************************************************************
* 기      능   : 사무소 별 협력업체 필터 
********************************************************************************/
function cb_lifnr_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	ds_cond.SetColumn(0,"ZZLIFNR",strCode);
	cb_ztemno.value = "";
    var nHRow = ds_zzactss.SearchRow("CODE=\"" +  ds_zzlifnr.GetColumn(nNewIndex, "ZZACTSS") + "\""); 
    ds_cond.SetColumn(0,"ZZACTSS",ds_zzactss.GetColumn(nHRow, "CODE"));
	tit_clearActionInfo();			
	tit_addSearchActionInfo("qm05:QM0503001A_S1");
	tit_callService(
		""
		, ""
		, "ds_cond=ds_cond"
		, "ds_ztemno_cd=ds_ztemno_cd"  
		, ""
		, "fn_afterZtemnoChange"  
		, ""
		, ""
		, true);
}
/********************************************************************************
* 기      능   : 조회 Callback  - 사무소 변경시
********************************************************************************/
function fn_afterZtemnoChange(errCode, errMsg) {
	cb_ztemno.Index = 0;
	gfn_insertEmptyRow( ds_ztemno_cd, G_CODE_ALL );
	cb_ztemno.value = "";
}
/********************************************************************************
* 기      능   : 사무소 별 협력업체 필터 
********************************************************************************/
function common_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if(cb_temno.Text == '- 전체 -')
    {
       ds_zzactss.UnFilter();
       ds_zzlifnr.UnFilter();
       ds_ztemno_cd.UnFilter();
       cb_zzactss.Index = 0;
       cb_lifnr.Index = 0;
       cb_ztemno.Index = 0;
    }
    else
    {
      var arr = ds_cond.GetColumn(0,"DTEXT4");
	   ds_zzactss.Filter("LENGTH(CODE) == 0 || DTEXT3 == "+quote(strCode));
	   cb_zzactss.Index = 0;
       cb_lifnr.Index = 0;
       cb_ztemno.Index = 0;
    }
}

