Window.Script:
/*
 ******************************************************************************************
 * 기      능   :
 * 작  성  자   :
 * 작성  일자   :
 * ----------------------------------------------------------------------------------------
 * HISTORY    :
 ******************************************************************************************
 */
#include "LIB::commons.js";	// 공통 스크립트
//=========================================================================================
// [ PART 1 ]
// Form에서 사용될 전역변수를 선언
// FORM에서 사용되는 전역변수는 대문자로 작성하며, F_XXX 의 형태를 갖도록 한다.
//=========================================================================================
var _P_INP_DT = '';
var _P_ZZACTSS = '';
var _P_ZZLIFNR = '';
var _P_ZZPMNUM = '';
var _P_NAMET = '';
//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술
	gfn_initForm();

	trace('_P_INP_DT : ' + _P_INP_DT);
	trace('_P_ZZACTSS : ' + _P_ZZACTSS);
	trace('_P_ZZLIFNR : ' + _P_ZZLIFNR);
	trace('_P_ZZPMNUM : ' + _P_ZZPMNUM);
	trace('_P_NAMET : ' + _P_NAMET);

	var today = gfn_getCurrDate();
	var curMonth = ToNumber(substr(today, 4, 2));
	var mdateMonth = ToNumber(substr(_P_INP_DT, 4, 2));
	if (curMonth > mdateMonth) {
		ds_cond.SetColumn(0, 'TBL', 'ZPSTW1714');
	} else {
		ds_cond.SetColumn(0, 'TBL', 'ZPSTW1713');
	}
	trace('TBL : ' + ds_cond.GetColumn(0, 'TBL'));

	ds_cond.SetColumn(0, 'INP_DT', _P_INP_DT);
	ds_cond.SetColumn(0, 'ZZACTSS', _P_ZZACTSS);
	ds_cond.SetColumn(0, 'ZZLIFNR', _P_ZZLIFNR);
	ds_cond.SetColumn(0, 'ZZPMNUM', _P_ZZPMNUM);
	ds_cond.SetColumn(0, 'NAMET', _P_NAMET);

	if (Length(_P_ZZACTSS) > 0 && Length(_P_ZZLIFNR) > 0 && Length(_P_ZZPMNUM) > 0) {
		div_btn.st_title.value = '소장담당' + this.title;
	} else if (Length(_P_ZZACTSS) > 0 && Length(_P_ZZLIFNR) > 0) {
		div_btn.st_title.value = '업체담당' + this.title;
	} else if (Length(_P_ZZACTSS) > 0) {
		div_btn.st_title.value = '지사담당' + this.title;
	}
	trace('div_btn.st_title.value : ' + div_btn.st_title.value);

	fn_query();
}
/********************************************************************************
* 기      능   : form UnloadCompleted
********************************************************************************/
function fn_OnUnloadCompleted(obj) {
	return gfn_isFormUnload(obj);
}
//=========================================================================================
// [ PART 3 ]
// Main Process Event 정의(조회,입력,삭제,저장,인쇄,form닫기)
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
function fn_query() {
	tit_clearActionInfo();
    tit_addSearchActionInfo("ps17:PS1701001E_S1");
	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true);
}

/********************************************************************************
* 기      능   : 닫기
********************************************************************************/
function fn_close() {
	close(null);
}

/********************************************************************************
* 기      능   : 조회 Callback
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	ds_list.Filter("STAT > " + quote(''));
	// 메시지 처리
	gfn_showSearchMsg(grd_list);
}

function grd_list_OnCellDBLClick(obj,nRow,nCell,nX,nY,nPivotIndex) {
	var pspid = ds_list.GetColumn(nRow, 'PSPID');
	var posid = ds_list.GetColumn(nRow, 'POSID');
	if (Length(pspid) > 0) {
		var args = 'F_PSPID=' + quote(pspid);
		if (Length(posid) > 0) {
			args += ' _P_POSID=' + quote(posid);
		}
		gfn_openWin("PS0209001A", args);
		fn_close();
	}
}

function grd_list_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex) {

}

function fn_excel()
{
	gfn_saveToExcel(grd_list, true);
}
