function fn_query()
{
	tit_clearActionInfo();
	tit_addSearchActionInfo("ps17:PS1701002A_S1");	// #기종별 조회
	tit_addSearchActionInfo("ps17:PS1701002A_S2");	// #지역별 조회
	tit_addSearchActionInfo("ps17:PS1701002A_S3");	// #건설사1 - 건물용도
	tit_addSearchActionInfo("ps17:PS1701002A_S4");  // #건설사2 - 건설사(시공사)
	tit_addSearchActionInfo("ps17:PS1701002A_S5");  // #건설사3 - 시행사

	if(Tab00.tab4.rd_gbn == 'A')	// 진행호기
	{	
		tit_addSearchActionInfo("ps17:PS1701002A_S6");  // #로드분배 - 진행호기
	}
	else	// 미진행호기
	{
		tit_addSearchActionInfo("ps17:PS1701002A_S8");  // #로드분배 - 미진행호기
	}



	tit_callService(
          ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list1=ds_list1 ds_list2=ds_list2 ds_list3=ds_list3 ds_list4=ds_list4 ds_list5=ds_list5 ds_list6=ds_list6"
        , ""
        , "fn_afterQuery"
        , true);

}



