/********************************************************************************
* 기      능   : 액셀
********************************************************************************/
function fn_excel()
{

	switch (Tab00.TabIndex)
	{
		case 0 :gfn_saveToExcel(Tab00.tab1.grd_list, true, '', '', '', true);	// #기종별
				break;
		case 1 :gfn_saveToExcel(Tab00.tab2.grd_list, true, '', '', '', true);	// #지역별
				break;
		case 2 :gfn_saveToExcel(Tab00.tab3.grd_list3, true, '', '', '', true);	// #지역별
				break;
		case 3 :gfn_saveToExcel(Tab00.tab4.grd_list, true, '', '', '', true);	// #지역별
				break;
	}

}


