/********************************************************************************
* 기      능   : 추가
********************************************************************************/
function fn_insert(obj)
{
	var nAddRow = 0;

	if(Tab00.TabIndex == 0)	// 기종별
	{
		nAddRow = ds_list1.AddRow();
		//Trace(ds_list1.GetRowType(nAddRow));
	}
	else if(Tab00.TabIndex == 2)	// 건설사
	{
		if(obj.id == 'btn_add')	// 건물용도
		{
			nAddRow = ds_list3.AddRow();
		}		
		else if(obj.id == 'btn_add2')	// 시행사
		{	
			nAddRow = ds_list4.AddRow();
		}
		else	// 건설사(시공사)
		{
			nAddRow = ds_list5.AddRow();
		}
	}

}

/********************************************************************************
* 기      능   : 삭제
********************************************************************************/
function fn_del(obj)
{
	var nSelectRow = 0;

	if(Tab00.TabIndex == 0)
	{
		nSelectRow = ds_list1.rowposition;

		if(ds_list1.GetRowType(nSelectRow) <> 'insert')
		{
			return;
		}
		else
		{
			ds_list1.DeleteRow(nSelectRow);
		}
	}
	else if(Tab00.TabIndex == 2)	// 건설사
	{
		if(obj.id == 'btn_del')	// 건물용도
		{
			nSelectRow = ds_list3.rowposition;

			if(ds_list3.GetRowType(nSelectRow) <> 'insert')
			{
				return;
			}
			else
			{
				ds_list3.DeleteRow(nSelectRow);
			}
		}		
		else if(obj.id == 'btn_del2')	// 시행사
		{	
			nSelectRow = ds_list4.rowposition;

			if(ds_list4.GetRowType(nSelectRow) <> 'insert')
			{
				return;
			}
			else
			{
				ds_list4.DeleteRow(nSelectRow);
			}
		}
		else	// 건설사(시공사)
		{
			nSelectRow = ds_list5.rowposition;

			if(ds_list5.GetRowType(nSelectRow) <> 'insert')
			{
				return;
			}
			else
			{
				ds_list5.DeleteRow(nSelectRow);
			}
		}
	}
}

/********************************************************************************
* 기      능   : 저장
********************************************************************************/
function fn_save()
{
	Trace(gfn_isChangeDs(ds_list1));

	if(!gfn_isChangeDs(ds_list1) && !gfn_isChangeDs(ds_list2) && !gfn_isChangeDs(ds_list3) && !gfn_isChangeDs(ds_list4) && !gfn_isChangeDs(ds_list5) && !gfn_isChangeDs(ds_list6))
	{
		gfn_openMsgWin("변경사항이 존재하지 않습니다.");
		return;
	}

	var nAddRow = 0;

	if(Tab00.TabIndex == 0) // ## 기종별
	{

		ds_list1_tmp.ClearData();

		for(var i=0; i<ds_list1.GetRowCount();i++)
		{
			if(ds_list1.GetRowType(i) == 'insert' || ds_list1.GetRowType(i) == 'update')
			{
				var sZspec1 = ds_list1.GetColumn(i, "ZSPEC1");
				var sRev    = ds_list1.GetColumn(i, "REV");
				if(Length(Trim(sZspec1)) == 0 || Length(Trim(sRev)) == 0)
				{
					gfn_openMsgWin("기종 또는 Rev는 필수입력조건 입니다.");
					return;
				}

				nAddRow = ds_list1_tmp.AddRow();
				ds_list1_tmp.CopyRow(nAddRow, ds_list1, i);
			}
		}
		//Trace(ds_list1_tmp.saveXML());

		tit_clearActionInfo();


		tit_addSelectExActionInfo("ps17:PS1701002A_S11", "ps17:PS1701002A_I1", "ps17:PS1701002A_U1");	// ZPSTW1707 INSERT OR UPDATE.
		tit_callService(

		""
		, ""
		, "ds_list1=ds_list1_tmp"
		, ""
		, ""
		, "fn_afterSave"
		, ""
		, ""
		, true  // ""

		);
	}
	else if(Tab00.TabIndex == 1) // #지역별
	{
		ds_list2_tmp.ClearData();


		for(var i=0; i<ds_list2.GetRowCount();i++)
		{

			if(ds_list2.GetRowType(i) == 'update')
			{
				nAddRow = ds_list2_tmp.AddRow();
				ds_list2_tmp.CopyRow(nAddRow, ds_list2, i);
			}
		}
		//Trace(ds_list2_tmp.saveXML());

		tit_clearActionInfo();


		tit_addSelectExActionInfo("ps17:PS1701002A_S21", "ps17:PS1701002A_I2", "ps17:PS1701002A_U2");	// ZPSTW1707 INSERT OR UPDATE.
		tit_callService(

		""
		, ""
		, "ds_list2=ds_list2_tmp"
		, ""
		, ""
		, "fn_afterSave"
		, ""
		, ""
		, true  // ""

		);
	}
	else if(Tab00.TabIndex == 2) // #건설사
	{
		ds_list3_tmp.ClearData();
		ds_list4_tmp.ClearData();
		ds_list5_tmp.ClearData();

		for(var i=0; i<ds_list3.GetRowCount();i++)
		{

			if(ds_list3.GetRowType(i) == 'insert' || ds_list3.GetRowType(i) == 'update')
			{
				var sWwbld  = ds_list3.GetColumn(i, "WWBLD");
				var sRev    = ds_list3.GetColumn(i, "REV");
				if(Length(Trim(sWwbld)) == 0 || Length(Trim(sRev)) == 0)
				{
					gfn_openMsgWin("건물용도 또는 비율(%)은 필수입력조건 입니다.");
					return;
				}

				nAddRow = ds_list3_tmp.AddRow();
				ds_list3_tmp.CopyRow(nAddRow, ds_list3, i);
			}
		}

		for(var i=0; i<ds_list4.GetRowCount();i++)
		{
			var sEnfor  = ds_list4.GetColumn(i, "ENFOR");
			var sRev    = ds_list4.GetColumn(i, "REV");
			if(Length(Trim(sEnfor)) == 0 || Length(Trim(sRev)) == 0)
			{
				gfn_openMsgWin("시행사 또는 비율(%)은 필수입력조건 입니다.");
				return;
			}

			if(ds_list4.GetRowType(i) == 'insert' || ds_list4.GetRowType(i) == 'update')
			{
				nAddRow = ds_list4_tmp.AddRow();
				ds_list4_tmp.CopyRow(nAddRow, ds_list4, i);
			}
		}

		for(var i=0; i<ds_list5.GetRowCount();i++)
		{
			var sKunnr  = ds_list5.GetColumn(i, "KUNNR");
			var sRev    = ds_list5.GetColumn(i, "REV");
			if(Length(Trim(sKunnr)) == 0 || Length(Trim(sRev)) == 0)
			{
				gfn_openMsgWin("건설사(시공사) 또는 비율(%)은 필수입력조건 입니다.");
				return;
			}

			if(ds_list5.GetRowType(i) == 'insert' || ds_list5.GetRowType(i) == 'update')
			{
				nAddRow = ds_list5_tmp.AddRow();
				ds_list5_tmp.CopyRow(nAddRow, ds_list5, i);
			}
		}

		//Trace(ds_list3_tmp.saveXML());

		tit_clearActionInfo();


		tit_addSelectExActionInfo("ps17:PS1701002A_S31", "ps17:PS1701002A_I3", "ps17:PS1701002A_U3");	// ZPSTW1707 INSERT OR UPDATE
		tit_addSelectExActionInfo("ps17:PS1701002A_S41", "ps17:PS1701002A_I4", "ps17:PS1701002A_U4");	// ZPSTW1707 INSERT OR UPDATE.
		tit_addSelectExActionInfo("ps17:PS1701002A_S51", "ps17:PS1701002A_I5", "ps17:PS1701002A_U5");	// ZPSTW1707 INSERT OR UPDATE.
		tit_callService(

		""
		, ""
		, "ds_list3=ds_list3_tmp ds_list4=ds_list4_tmp ds_list5=ds_list5_tmp"
		, ""
		, ""
		, "fn_afterSave"
		, ""
		, ""
		, true  // ""

		);
	}
	else if(Tab00.TabIndex == 3) // #진행호기/미진행호기
	{

		ds_list6_tmp.ClearData();

		for(var i=0; i<ds_list6.GetRowCount();i++)
		{

			if(ds_list6.GetRowType(i) == 'update')
			{
				nAddRow = ds_list6_tmp.AddRow();
				ds_list6_tmp.CopyRow(nAddRow, ds_list6, i);
			}
		}



		tit_clearActionInfo();

		if(Tab00.tab4.rd_gbn == 'A')	// 진행호기
		{	
			tit_addMultiActionInfo("ps17:PS1701002A_U6");
		}
		else	// 미진행호기
		{
			tit_addMultiActionInfo("ps17:PS1701002A_U7");
		}

		tit_callService(

		""
		, ""
		, "ds_list6=ds_list6_tmp"
		, ""
		, ""
		, "fn_afterSave"
		, ""
		, ""
		, true  // ""

		);





	}



}

/********************************************************************************
* 기      능   : 저장 후  Callback
********************************************************************************/
function fn_afterSave(errCode, errMsg)
{
	if ( errCode!= 0 ) {
	//  CE00001 : "처리에 실패했습니다.\n확인 후 다시 처리해 주십시오.");'
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("저장처리가 완료되었습니다.");
		fn_query();

	}
}

/********************************************************************************
* 기      능   : 삭제
********************************************************************************/
function fn_delete()
{
	//Trace(gfn_isChangeDs(ds_list1));

	if(!gfn_isChangeDs(ds_list1) && !gfn_isChangeDs(ds_list3) && !gfn_isChangeDs(ds_list4) && !gfn_isChangeDs(ds_list5))
	{
		gfn_openMsgWin("변경사항이 존재하지 않습니다.");
		return;
	}

	var nAddRow = 0;

	if(Tab00.TabIndex == 0)	// ## 기종별
	{
		ds_list1_tmp.ClearData();


		for(var i=0; i<ds_list1.GetRowCount();i++)
		{
			if(ds_list1.GetColumn(i, "CHK") == '1')
			{
				nAddRow = ds_list1_tmp.AddRow();
				ds_list1_tmp.CopyRow(nAddRow, ds_list1, i);
			}
		}
		//Trace(ds_list1_tmp.saveXML());

		tit_clearActionInfo();


		tit_addMultiActionInfo("ps17:PS1701002A_D1");
		tit_callService(

		""
		, ""
		, "ds_list1=ds_list1_tmp"
		, ""
		, ""
		, "fn_afterDel1"
		, ""
		, ""
		, true  // ""

		);
	}
	else if(Tab00.TabIndex == 2)	// ## 건설사별
	{
		var chk3 = 0;
		var chk4 = 0;
		var chk5 = 0;

		ds_list3_tmp.ClearData();
		ds_list4_tmp.ClearData();
		ds_list5_tmp.ClearData();

		for(var i=0; i<ds_list3.GetRowCount();i++)
		{
			if(ds_list3.GetColumn(i, "CHK") == '1')
			{
				nAddRow = ds_list3_tmp.AddRow();
				ds_list3_tmp.CopyRow(nAddRow, ds_list3, i);
				chk3++;
			}
		}

		for(var i=0; i<ds_list4.GetRowCount();i++)
		{
			if(ds_list4.GetColumn(i, "CHK") == '1')
			{
				nAddRow = ds_list4_tmp.AddRow();
				ds_list4_tmp.CopyRow(nAddRow, ds_list4, i);
				chk4++;
			}
		}

		for(var i=0; i<ds_list5.GetRowCount();i++)
		{
			if(ds_list5.GetColumn(i, "CHK") == '1')
			{
				nAddRow = ds_list5_tmp.AddRow();
				ds_list5_tmp.CopyRow(nAddRow, ds_list5, i);
				chk5++;
			}
		}
		//Trace(ds_list1_tmp.saveXML());

		tit_clearActionInfo();

		if(chk3 > 0)
		{
			tit_addMultiActionInfo("ps17:PS1701002A_D3");
		}
		if(chk4 > 0)
		{
			tit_addMultiActionInfo("ps17:PS1701002A_D4");
		}

		if(chk5 > 0)
		{
			tit_addMultiActionInfo("ps17:PS1701002A_D5");
		}

		if( chk3 > 0 || chk4> 0 || chk5 > 0)
		{
			tit_callService(

			""
			, ""
			, "ds_list3=ds_list3_tmp ds_list4=ds_list4_tmp ds_list5=ds_list5_tmp"
			, ""
			, ""
			, "fn_afterDel1"
			, ""
			, ""
			, true  // ""

			);
		}

	}



}

/********************************************************************************
* 기      능   : 저장 후  Callback
********************************************************************************/
function fn_afterDel1(errCode, errMsg)
{
	if ( errCode!= 0 ) {
	//  CE00001 : "처리에 실패했습니다.\n확인 후 다시 처리해 주십시오.");'
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("삭제처리가 완료되었습니다.");
		fn_query();

	}
}



function ds_list3_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID == 'WWBLD')
	{
		var nFindRow = ds_list3.SearchRow("WWBLD == '"+varNewValue+"'");
		if(nFindRow >= 0 && nFindRow != nRow)
		{
			gfn_openMsgWin("건물용도는 중복선택 불가합니다.");
			ds_list3.fireevent = false;
			ds_list3.SetColumn(nRow, "WWBLD", '');
			ds_list3.SetColumn(nRow, "BIGO", '');
			ds_list3.fireevent = true;
			return false;
		}

		nFindRow = ds_wwbld.SearchRow("CODE == '"+varNewValue+"'");
		if(nFindRow >= 0)
		{
			ds_list3.SetColumn(nRow, "BIGO", ds_wwbld.GetColumn(nFindRow, "CODE_NM"));
		}
	}
}

//건설사(시공사) 팝업
function Tab00_tab3_Grid1_OnExpandUp(obj,nRow,nCell,strVal,nPivotIndex)
{
	var arg = "";
	var arr = gfn_openPopup("PS1701002B", true, arg);

	if(Length(arr) > 0)
	{
		//ds_list.fireevent = false;
		ds_list5.SetColumn(nRow, "KUNNR", arr[0]);
		ds_list5.SetColumn(nRow, "BIGO", arr[1]);
		//ds_list.fireevent = true;

		var nFindRow = ds_list5.SearchRow("KUNNR == '"+arr[0]+"'");
		Trace(nFindRow);
		if(nFindRow >= 0 && nFindRow != nRow)
		{
			gfn_openMsgWin("건설사(시행사)는 중복선택 불가합니다.");
			ds_list5.fireevent = false;
			ds_list5.SetColumn(nRow, "KUNNR", '');
			ds_list5.SetColumn(nRow, "BIGO", '');
			ds_list5.fireevent = true;
			return false;
		}

	}
}


