/********************************************************************************
* 기      능   : 전체복사
********************************************************************************/
function fn_copyAll(frRow,toRow,arrCols,gbn){
	var toRows;
	var	arrTo = split(toRow, ',');

	for(var i=0; i<length(arrTo); i++){
		toRows = trim(arrTo[i]);

		if(toRows=='') continue;

		if(isDigit(toRows)){
			if(frRow == toRows){
				gfn_showMsg('CW00129');
				// alert("복사 할 번호와 붙여넣기 할 번호는 달라야 합니다.");
				return;
			}
			if(gbn == "col") {
				fn_copyCol(targetGrid, toNumber(frRow)-1, toNumber(toRows)-1, '0',arrCols);
			} else if(gbn == "row") {
				fn_copyRow(targetGrid, toNumber(frRow)-1, toNumber(toRows)-1, '0',arrCols);
			}
		} else {
			if(IndexOf(toRows, "-")!=-1){
				var toList = split(toRows, '-');

				if(length(toList)<2){
					if(!isDigit(toRows)){
						gfn_showMsg('CW00130');
						// alert("[ , ], [ - ] 제외한 특수문자가 있거나 문장형식이 올바르지 않습니다.");
						return;
					}
				}
				if(length(toList)>2) {
					gfn_showMsg('CW00131');
					// alert("[ - ] 또는 [ , ] 는 연속해서 사용할 수 없습니다.");
					return;
				}

				var tomin = toList[0];
				var tomax = toList[1];

				if(!isDigit(tomin) || !isDigit(tomax)){
					gfn_showMsg('CW00130');
					// alert("[ , ], [ - ] 제외한 특수문자가 있거나 문장형식이 올바르지 않습니다.");
					return;
				}
				if(toNumber(tomin) > toNumber(tomax)){
					gfn_showMsg('CW00128');
					// alert("시작줄은 완료줄보다 작아야 합니다.");
					return;
				}
				if(frRow==tomin || frRow==tomax){
					gfn_showMsg('CW00129');
					// alert("복사 할 번호와 붙여넣기 할 번호는 달라야 합니다.");
					return;
				}
				if(gbn == "col") {
					fn_copyCol(targetGrid, toNumber(frRow)-1, toNumber(tomin)-1, toNumber(tomax)-1,arrCols);
				} else if(gbn == "row") {
					fn_copyRow(targetGrid, toNumber(frRow)-1, toNumber(tomin)-1, toNumber(tomax)-1,arrCols);
				}
			} else {
				gfn_showMsg('CW00130');
				// alert("[ , ], [ - ] 제외한 특수문자가 있거나 문장형식이 올바르지 않습니다.");
				return;
			}
		}
	}
}
/********************************************************************************
* 기      능   : prarm(해당그리드,
                       Source Dataset의 복사 할 Record Index,
                       Target Dataset의 복사되어 변경 될 첫번째 Row Index,
                       Target Dataset의 복사되어 변경 될 마지막 Row Index,
                       복사할 칼럼
********************************************************************************/
function fn_copyRow(gridObj, frRow, toRow0, toRow1,cols){
	var dsObj = object(gridObj.bindDataset);
	N_COPY = "Y"; //DATASET변경시 FLAG가 'Y'인 경우 휴일체크 안함
	gridObj.Redraw = false;
	//dsObj.fireEvent = false;
	if(tGrid == 'Tab00.tab3.grd_list')
	{
		if(toRow1=='0'){
			if( IndexOf(cols, "1") != -1 ) {
				ds_list4.SetColumn(toRow0,"ZZSHIP1",ds_list4.GetColumn(frRow,"ZZSHIP1"));
			}
			if( IndexOf(cols, "2") != -1  && length(ds_list4.GetColumn(toRow0,"AUFNR2")) > 0) {
				ds_list4.SetColumn(toRow0,"ZZSHIP2",ds_list4.GetColumn(frRow,"ZZSHIP2"));
			}
			if( IndexOf(cols, "3") != -1  && length(ds_list4.GetColumn(toRow0,"AUFNR3")) > 0) {
