					continue;
				}

				if( userGroup == '10' || userGroup == '30')
				{
					if( IndexOf(cols, "1") != -1 && length(ds_list5.GetColumn(i,"ZZFKEYD")) == 0 && length(ds_list5.GetColumn(i,"ZZSTAN1")) == 0) {
						ds_list5.SetColumn(i,"ZZSHIP1",ds_list5.GetColumn(frRow,"ZZSHIP1"));
					}
					if( IndexOf(cols, "2") != -1  && length(ds_list5.GetColumn(i,"AUFNR2")) > 0) {
						ds_list5.SetColumn(i,"ZZSHIP2",ds_list5.GetColumn(frRow,"ZZSHIP2"));
					}
					if( IndexOf(cols, "3") != -1  && length(ds_list5.GetColumn(i,"AUFNR3")) > 0) {
						ds_list5.SetColumn(i,"ZZSHIP3",ds_list5.GetColumn(frRow,"ZZSHIP3"));
					}
					if( IndexOf(cols, "4") != -1  && length(ds_list5.GetColumn(i,"AUFNR4")) > 0) {
						ds_list5.SetColumn(i,"ZZSHIP4",ds_list5.GetColumn(frRow,"ZZSHIP4"));
					}
					if( IndexOf(cols, "5") != -1  && length(ds_list5.GetColumn(i,"AUFNR5")) > 0) {
						ds_list5.SetColumn(i,"ZZSHIP5",ds_list5.GetColumn(frRow,"ZZSHIP5"));
					}
					if( IndexOf(cols, "6") != -1  && length(ds_list5.GetColumn(i,"AUFNR6")) > 0) {
						ds_list5.SetColumn(i,"ZZSHIP6",ds_list5.GetColumn(frRow,"ZZSHIP6"));
					}
					if( IndexOf(cols, "7") != -1 && length(ds_list5.GetColumn(i,"ZZSTAN1")) == 0 ) {
						ds_list5.SetColumn(i,"ZZCOMP2",ds_list5.GetColumn(frRow,"ZZCOMP2"));
					}
				}
				else if( userGroup == '40')
				{
					if( IndexOf(cols, "1") != -1 && length(ds_list5.GetColumn(i,"ZZCHAKG")) == 0 && length(ds_list5.GetColumn(i,"ZZSTAN1")) == 0 && length(ds_list5.GetColumn(i,"ZZFMAGA")) == 0) {
						ds_list5.SetColumn(i,"ZZSHIP1",ds_list5.GetColumn(frRow,"ZZSHIP1"));
					}
					if( IndexOf(cols, "2") != -1  && length(ds_list5.GetColumn(i,"AUFNR2")) > 0 && (length(ds_list5.GetColumn(i,"ZZSTAN1")) == 0 || trim(ds_list5.GetColumn(i,"ZZSTAN1")) == '0')) {
						ds_list5.SetColumn(i,"ZZSHIP2",ds_list5.GetColumn(frRow,"ZZSHIP2"));
					}
					if( IndexOf(cols, "3") != -1  && length(ds_list5.GetColumn(i,"AUFNR3")) > 0 && (length(ds_list5.GetColumn(i,"ZZSTAN1")) == 0 || trim(ds_list5.GetColumn(i,"ZZSTAN1")) == '0')) {
						ds_list5.SetColumn(i,"ZZSHIP3",ds_list5.GetColumn(frRow,"ZZSHIP3"));
					}
					if( IndexOf(cols, "4") != -1  && length(ds_list5.GetColumn(i,"AUFNR4")) > 0 && (length(ds_list5.GetColumn(i,"ZZSTAN1")) == 0 || trim(ds_list5.GetColumn(i,"ZZSTAN1")) == '0')) {
						ds_list5.SetColumn(i,"ZZSHIP4",ds_list5.GetColumn(frRow,"ZZSHIP4"));
					}
					if( IndexOf(cols, "5") != -1  && length(ds_list5.GetColumn(i,"AUFNR5")) > 0 && (length(ds_list5.GetColumn(i,"ZZSTAN1")) == 0 || trim(ds_list5.GetColumn(i,"ZZSTAN1")) == '0')) {
						ds_list5.SetColumn(i,"ZZSHIP5",ds_list5.GetColumn(frRow,"ZZSHIP5"));
					}
					if( IndexOf(cols, "6") != -1  && length(ds_list5.GetColumn(i,"AUFNR6")) > 0) {
						ds_list5.SetColumn(i,"ZZSHIP6",ds_list5.GetColumn(frRow,"ZZSHIP6"));
					}
					if( IndexOf(cols, "7") != -1 && length(ds_list5.GetColumn(i,"ZZJUNGO")) == 0 && length(ds_list5.GetColumn(i,"ZZSTAN2")) == 0) {
						ds_list5.SetColumn(i,"ZZCOMP2",ds_list5.GetColumn(frRow,"ZZCOMP2"));
					}
				}

				//설치PM, 협력업체, 작업팀장 복사
				if( userGroup == '30' || userGroup == '40')
				{
					if( IndexOf(cols, "9") != -1 && ds_list5.GetColumn(frRow,"ZZACTSS") == ds_list5.GetColumn(i,"ZZACTSS") ) {
						ds_list5.SetColumn(i,"ZZPMNUM",ds_list5.GetColumn(frRow,"ZZPMNUM"));
					}
					if( IndexOf(cols, "A") != -1 && ds_list5.GetColumn(i,"CHECK") != 'X' ) {
						ds_list5.SetColumn(i,"ZZLIFNR",ds_list5.GetColumn(frRow,"ZZLIFNR"));
					}
					if( IndexOf(cols, "C") != -1 && ds_list5.GetColumn(i,"CHECK") != 'X' ) {
						ds_list5.SetColumn(i,"ZZLIFNR2",ds_list5.GetColumn(frRow,"ZZLIFNR2"));
					}
				}
				if( IndexOf(cols, "B") != -1 && ds_list5.GetColumn(frRow,"ZZLIFNR") == ds_list5.GetColumn(i,"ZZLIFNR") ) {
					ds_list5.SetColumn(i,"ZZTEMNO",ds_list5.GetColumn(frRow,"ZZTEMNO"));
				}
				if( IndexOf(cols, "D") != -1 && ds_list5.GetColumn(frRow,"ZZLIFNR2") == ds_list5.GetColumn(i,"ZZLIFNR2") ) {
					ds_list5.SetColumn(i,"ZZTEMNO2",ds_list5.GetColumn(frRow,"ZZTEMNO2"));
				}
			}
		}
	}

	gfn_showMsg("CI00005");

	//dsObj.fireEvent = true;
	gridObj.Redraw = true;
	N_COPY = "N";
}
