function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);
	// 공급업체 보여주기 
	ed_lifnrName.value = G_VEND_NAME;
	ds_cond.setColumn(0, "LIFNR", G_VEND_CODE_M);

	ufn_enableBtn("delete", false);
	ufn_enableBtn("save", false);
	ufn_enableBtn("excel", false);

	cal_eindtf.Value = substr(gfn_getCurrDate(),0,6)+"01";
	cal_eindtt.Value = gfn_getCurrDate();	

	gfn_showMsg("CI00007");
}
