/********************************************************************************
* 기      능   : 조회 
********************************************************************************/
function fn_query() 
{
	// 필수 입력사항 및 일자 체크 
	if ( !gfn_checkFromToObj(ed_rphogif, ed_rphogit, true, st_rphogi.value) ) 
	{
		return;
	}
	if ( !gfn_checkFromToObj(ed_invnrf, ed_invnrt, true, st_invnr.value, 10) ) 
	{
		return;
	}
	if ( !gfn_checkFromToObj(cal_eindtf, cal_eindtt, true, st_eindt.value, 0, true) ) 
	{
		return;
	}
	if ( !gfn_checkFromToObj(cal_parqdtf, cal_parqdtt, true, st_parqdt.value) ) 
	{
		return;
	}
	if ( !gfn_checkFromToObj(cal_pacfdtf, cal_pacfdtt, true, st_pacfdt.value) ) 
	{
		return;
	}
	if ( !gfn_checkFromToObj(cal_paprdtf, cal_paprdtt, true, st_paprdt.value) ) 
	{
		return;
	}

	// sort mark 삭제 
	gfn_clearGridSortMark(grd_list);
	ds_list.row = -1;
	// 조회 처리 
	tit_callService(
        "QM0101002A_ACT"
        , "query"
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , false);
}
