/********************************************************************************
* 기      능   : Delete 
********************************************************************************/
function fn_delete() 
{
	if ( ds_list.rowCount() == 0 ) 
	{
		// 삭제할 자료 선택 
		// CW00013=처리할 자료를 먼저 선택해 주십시오.
		gfn_showMsg("CW00013");
		return;
	}

	if( toNumber(ds_list.getColumn(ds_list.row, "STATUS")) > 3) 
	{

	} 
	else 
	{
		// 삭제 처리 
		ds_list.setColumn(ds_list.row, "FLAG", "D");
	}
}
/********************************************************************************
* 기      능   : 저장 
********************************************************************************/
function fn_save() {
	if ( !gfn_isChangeDs(ds_list, true, "FLAG") ) {
		return;
	}

//	ds_list.Filter("FLAG=='I' || FLAG=='U' || FLAG=='D'");
//	ds_list2.copyF(ds_list);
//	ds_list.Filter("1==1");

	// 검사요청일 정보 확인 - 현재일자 포함 이후 일자만 가능
	var isFalse = false;
	for ( var i = 0; i < ds_list.rowCount(); i++ ) {
		if( ds_list.getColumn(i, "FLAG") == "I" 
			|| ds_list.getColumn(i, "FLAG") == "U" ) {
			// 일자 확인 
			if ( length( ds_list.getColumn(i, "PARQDT")) == 0 ) {
				gfn_showMsg("CW00002");
				isFalse = true;
			} else if ( toNumber( gfn_getCurrDate() ) > toNumber(ds_list.getColumn(i, "PARQDT")) ) {
				// CW00007=${}은/는 ${} 보다 같거나 이후 일자로 입력하여 주십시오.
				var arr = [st_parqdt.value, st_currDate.value];
				gfn_showMsg("CW00007", arr);
				isFalse = true;
			}
			if ( isFalse ) {
				ds_list.row = i;
				grd_list.setCellPos(gfn_getColIdx(grd_list, "PARQDT"));
				grd_list.setFocus();
				return;
			}
		}
	}

	// 저장 처리 
	tit_callService(
        "QM0101002A_ACT"
        , "save"
        , "ds_list=ds_list:A ds_cond=ds_cond_bak"
        , "ds_error=ds_error ds_list=ds_list"
        , ""
        , "fn_afterSave");
}
