// [ PART 4]
// Callback 함수 정의 : callback 함수명은 메인 함수의 fn_xxxx() 에서 xxxx 부분은 fn_afterXXXX()
// 형태가 되도록 작성 
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	// 메시지 처리 
	if ( !gfn_showSearchMsg(grd_list, errCode, errMsg) ) {
		return;
	}

	if( chk_status1.value == "X" ) {
		// 선택했을 경우 
		ufn_enableBtn("delete", false);
	} else {
		ufn_enableBtn("delete", true);
	}

	ds_cond_bak.copy(ds_cond);
	if ( ds_list.rowCount() > 0 ) {
		ufn_enableBtn("save", true);
		ufn_enableBtn("excel", true);
	} else {
		ufn_enableBtn("save", false);
		ufn_enableBtn("excel", false);
	}
}
/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave(errCode, errMsg) {
	gfn_showWSSaveMsgForQM(grd_list, ds_error, "FLAG", errCode, errMsg);
}
