/********************************************************************************
* 기      능   : 체크박스 확인 - 거래작성은 거래작성 정보만 조회되도록 
********************************************************************************/
function chk_OnClick(obj,strValue)
{
	var isNotOne = true;
	if ( obj.id == "chk_status1" && strValue == "X") {
		isNotOne = false;
	}

	cal_parqdtf.Enable = isNotOne;
	cal_parqdtt.Enable = isNotOne;
	cal_pacfdtf.Enable = isNotOne;
	cal_pacfdtt.Enable = isNotOne;
	cal_paprdtf.Enable = isNotOne;
	cal_paprdtt.Enable = isNotOne;

	if ( !isNotOne ) {
		cal_parqdtf.value = "";
		cal_parqdtt.value = "";
		cal_pacfdtf.value = "";
		cal_pacfdtt.value = "";
		cal_paprdtf.value = "";
		cal_paprdtt.value = "";

		chk_status2.value = "";
		chk_status3.value = "";
		chk_status4.value = "";
		chk_status5.value = "";
		chk_status1.value = strValue;
	} else {
		chk_status1.value = "";
	}

	if ( ( chk_status1.value + chk_status2.value 
		+ chk_status3.value + chk_status4.value 
		+ chk_status5.value ) == "0" ) {
		chk_status1.value = "X";	
	}
}
/********************************************************************************
* 기      능   : 그리드 헤더 클릭시 정렬 처리 
********************************************************************************/
function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 0 ) {
		// 현재 처리할 format 기억
		gfn_selectGridCol(obj);
	} else {
		gfn_sortGrid(obj,nCell);
	}
}
/********************************************************************************
* 기      능   : 데이터셋 값 변경 시 - Flag 처리 
********************************************************************************/
function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	switch( strColumnID ) {
		case "FLAG":
			break;
		case "PARQDT":	// 검사 요청일 
			if ( varOldValue == varNewValue ) {
				return;
			}
			if( obj.getColumn(nRow, "STATUS") == "1") {
				obj.setColumn(nRow, "FLAG", "I");
			} else {
				obj.setColumn(nRow, "FLAG", "U");
			}
			break;
	}
}
/********************************************************************************
* 기      능   : 데이터셋 row 변경 시
********************************************************************************/
function ds_list_OnRowPosChanged(obj,nOldRow,nRow)
{
	// 삭제버튼 비활성화 
	if ( toNumber(ds_list.getColumn(nRow, "STATUS")) > 3 ) {
		ufn_enableBtn("delete", false);
	} else {
		ufn_enableBtn("delete", true);
	}

	if ( ds_list.getColumn(nRow, "PRUEFLOS") == "000000000000" ) {
		gfn_enable(div_detail.cal_rarqdt, true);
	} else {
		gfn_enable(div_detail.cal_rarqdt, false);
	}

	if ( ds_list.getColumn(nRow, "STATUS") == "1"
		|| ds_list.getColumn(nRow, "STATUS") == "2") {
		gfn_enable(div_detail.cal_rarqdt, true);
	} else {
		gfn_enable(div_detail.cal_rarqdt, false);
	}
}
// 달력 처리 부분 
/********************************************************************************
* 기      능   : 그리드에서 날짜 입력시 
********************************************************************************
function grd_list_OnExpandEdit(obj,nRow,nCell,strVal,nPivotIndex)
{
	var rect = obj.GetCellRect(nRow, ncell);

	cal_temp.top = rect[1] + 140;
	cal_temp.Left = rect[0] + 20; 
	cal_temp.Visible = true;
	cal_temp.value = ds_list.getColumn(nRow, "PARQDT");
	cal_temp.SetFocus();
}
/********************************************************************************
* 기      능   : 달력에서 일자 선택했을 경우 
********************************************************************************
function cal_temp_OnDayClick(obj,strText)
{
	ds_list.SetColumn(ds_list.row, "PARQDT", strText);
	obj.Visible = false;
}
/********************************************************************************
* 기      능   : 달력에서 포커스 잃었을 경우 
********************************************************************************
function cal_temp_OnKillFocus(obj)
{
	obj.Visible = false;
}
/********************************************************************************
* 기      능   : Cell 선택 시 : OnExpandEdit 이벤트 처리가 되지 않음. 
********************************************************************************
function grd_list_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if ( obj.Format == "UPDATE" && nCell == 1 && ds_list.getColumn(nRow, "STATUS") == "2" ) {
		ds_list.row = nRow;
		grd_list_OnExpandEdit(obj,nRow,nCell,"",0);
	}
}
