var userGroup = "";
var imgUploadFlg = "VIEW"; //권한별 이미지 업로드 여부 "":업로드, "VIEW":보기
var F_ADMIN_DEPT = "F7,F6";	// 효율개선부 , 정보기술실(관리부서) 		
var F_ADMIN_YN   = "Y";		// 관리부서 여부
