// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {
	gfn_initForm(true);

	userGroup = gfn_getUserGroup();

	ed_sTeam.Text = G_VEND_NAME;

	var codeArr = ["PS003"];
    var dsObjArr = [ds_zzactss];
    var addTypeArr = [G_CODE_ALL];
    gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);

	//
	rd_jpjt.Index = 0;
//	chk_s.Index = 0;
    //김대식 SW, 이찬규 BJ, 임진섭SW, 윤휘중KW  주차 조회조건추가
    if(G_USER_ID == "2021706" || G_USER_ID == "2022702"|| G_USER_ID == "2012928"|| G_USER_ID == "5005230" || G_USER_ID == "3013383") 
    {
       rd_jpjt.Visible = true;
       ed_jpjt.Visible = true;
    }
    else
    {
       rd_jpjt.Visible = false;
       ed_jpjt.Visible = false;
       ds_cond.SetColumn(0,"JPJT","B");
    }
    	//효율개선부만 날짜 수정 할 수 있도록 저장/취소 버튼 활성화 -- 정의성 sw요청
	if(G_DPT1 = "F7" || G_DPT1 = "F6")	{
	   btn_save.Enable  = true;  
       btn_cancel.Enable  = true;       }
	else{
	   btn_save.Enable  = false; 
	   btn_cancel.Enable  = false;      }

	fn_initQuery();
}
/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) {
	if (IndexOf(F_ADMIN_DEPT, G_DPT1) == -1)		{
		F_ADMIN_YN = "N";		
	}

	if(userGroup == '10')
	{//권한변경2017.07.25 효율개선부요청
		cb_zzactss.Index =  ds_zzactss.SearchRow("CODE=="+quote(G_AREA_CODE_E));
		cb_zzactss.Enable = false;
		imgUploadFlg = "";
	}
	else if(userGroup == '30')
	{
		cb_zzactss.Index = ds_zzactss.SearchRow("CODE=="+quote(G_AREA_CODE_E));
		cb_zzactss.Enable = true;
		imgUploadFlg = "DOWN";
	}
	else
	{
		cb_zzactss.Index = 0;
		cb_zzactss.Enable = true;
		imgUploadFlg = "DOWN";
	}
}
/********************************************************************************
* 기      능   : form UnloadCompleted 
********************************************************************************/
function fn_OnUnloadCompleted(obj) {
	return gfn_isFormUnload(obj);
}
