/********************************************************************************
* 기      능   : Delete 
********************************************************************************/
function fn_delete() {	
	ds_list_d.ClearData();
    ds_list_d2.ClearData();
	ds_list2.ClearData();
    ds_list3.ClearData();
	var rows = 0;
	for(var i=0;i<ds_list.GetRowCount();i++){

	    //==================================변경팀 추가===================================
	    if(ds_list.GetColumn(i,"EXAMREQUEST")=="1" && ds_list.GetColumn(i,"CHG_TEAM") == "" &&
		   ds_list.GetColumn(i,"PACFDT") > '00000000' )
	    {		
			gfn_showAlert("CW00001","변경팀");
			return false;
		}	    
	    //==========================================================f======================


		if(ds_list.GetColumn(i,"EXAMREQUEST") == "1") ds_list.SetColumn(i,"FLAG","D"); 

		if(ds_list.GetColumn(i,"EXAMREQUEST") == "1" &&
		   ds_list.GetColumn(i,"PACFDT") > '00000000')
		{
			ds_list2.AddRow();
			ds_list2.SetColumn(rows,"WERKS",ds_list.GetColumn(i,"WERKS"));
			ds_list2.SetColumn(rows,"PSPID",ds_list.GetColumn(i,"PSPID"));
			ds_list2.SetColumn(rows,"ART",ds_list.GetColumn(i,"ART"));
			ds_list2.SetColumn(rows,"HOGI",ds_list.GetColumn(i,"HOGI"));
			ds_list2.SetColumn(rows,"SEQ",ds_list.GetColumn(i,"SEQ"));
			ds_list2.SetColumn(rows,"PARQDT",ds_list.GetOrgColumn(i,"PARQDT"));
			ds_list2.SetColumn(rows,"PARQDT2","00000000");
			ds_list2.SetColumn(rows,"PACFDT",ds_list.GetColumn(i,"PACFDT"));
			ds_list2.SetColumn(rows,"EXEMPTION",ds_list.GetColumn(i,"EXEMPTION"));
			ds_list2.SetColumn(rows,"REQDT",ds_list.GetColumn(i,"REQDT"));
			ds_list2.SetColumn(rows,"REQTM",ds_list.GetColumn(i,"REQTM"));
			ds_list2.SetColumn(rows,"FLAG","I");
			ds_list2.SetColumn(rows,"DELCHK","X");
			//추가//
			ds_list2.SetColumn(rows,"CHG_TEAM",ds_list.GetColumn(i,"CHG_TEAM"));
			//====//
			rows++;
		}

		if(ds_list.GetColumn(i,"EXAMREQUEST") == "1"  &&
		   ds_list.GetColumn(i,"PARQDT") > '00000000'){

			if(ds_exam.GetRowCount()==0)	ds_exam.AddRow();

	   //대수확인	
           if( ds_list.GetColumn(i,"ZZACTSS") == ''|| ds_list.GetColumn(i,"ZZACTSS") == '01'|| ds_list.GetColumn(i,"ZZACTSS") == '02' || ds_list.GetColumn(i,"ZZACTSS") == '08' || ds_list.GetColumn(i,"ZZACTSS") == '10' 
                ||  ds_list.GetColumn(i,"ZZACTSS") == '14' || ds_list.GetColumn(i,"ZZACTSS") == '20' ||  ds_list.GetColumn(i,"ZZACTSS") == '19' ||  ds_list.GetColumn(i,"ZZACTSS") == '21')
             {  
                ds_exam.SetColumn(0,"ZZACTSS", '22')  ; //수도권으로 사무소 변경
             }
              else if (ds_list.GetColumn(i,"ZZACTSS") == '15' || ds_list.GetColumn(i,"ZZACTSS") == '16' )
             {  
                ds_exam.SetColumn(0,"ZZACTSS", '23')  ; //주차로 사무소  변경
             }    
              else
             {
                ds_exam.SetColumn(0,"ZZACTSS", ds_list.GetColumn(i,"ZZACTSS"))  ; 
             }

               ds_exam.SetColumn(0,"EXAMREQDATE",ds_list.GetColumn(i,"PARQDT"));

                tit_clearActionInfo();
		        tit_addSearchActionInfo("qm05:QM0501001A_S11");
		        tit_callService(
			     ""
			   , ""
			   , "ds_exam=ds_exam"
			   , "ds_list_d=ds_list_d"
			   , ""
			   , ""//"ufn_afterdaesuCount"
			   , ""
               , ""
			   , true);	
			  // alert(ds_list_d.GetRowCount());
			    if(ds_list_d.GetRowCount() >= 1 ){ //alert("a");
			      if(ds_list_d2.GetRowCount() == 0)
			      { //alert("b");
        	        ds_list_d.SetColumn(0,"DAESU_H", ds_list_d.GetColumn(0, "DAESU_H") - ds_list.GetColumn(i, "DAESU") );
        	        ds_list_d2.copyF(ds_list_d);
        	      }
			      else{  //alert("b");
			        for(var j=0;j<ds_list_d2.GetRowCount();j++)
                     { //alert("c");
                       if( (ds_list_d.GetColumn(0,"MDATE") == ds_list_d2.GetColumn(j,"MDATE")) && (ds_list_d.GetColumn(0,"ZZACTSS") == ds_list_d2.GetColumn(j,"ZZACTSS")) )
                       {//alert("w");

        	            ds_list_d2.SetColumn(j,"DAESU_H", ds_list_d2.GetColumn(j, "DAESU_H") - ds_list.GetColumn(i, "DAESU") );
                        ds_list_d.ClearData();
                       }
                        else
                        {//alert("d");
        	              ds_list_d.SetColumn(0,"DAESU_H", ds_list_d.GetColumn(0, "DAESU_H") - ds_list.GetColumn(i, "DAESU") );
                        }

                       }

                      ds_list_d2.AppendDataset(ds_list_d);
                  }

			    }

			 }


        }

       ds_list.Filter("FLAG =='D' && DAEGI == '1'");
	   ds_list3.copyF(ds_list);
	   ds_list.unFilter();


			  // alert("짠");
			  //trace( ds_list_d2.SaveXML());


	tit_clearActionInfo();
	tit_addSaveActionInfo(
		""
		, ""
		, "qm05:QM0501002A_D1"
		, "FLAG"
		, ""
		, -1
		, "");		

	if(ds_list2.rowcount > 0)
	{
		tit_addSaveActionInfo(
		"qm05:QM0501002A_I1"
		, ""
		, ""
		, "FLAG"
		, ""
		, -1
		, "");	
	}
	// 저장 처리 
	tit_callService(
        ""
        , ""
        , "ds_list=ds_list:A ds_list2=ds_list2:A"
        , ""
        , ""
        , "fn_afterDel");

}
/********************************************************************************
* 기      능   : 저장 
********************************************************************************/
function fn_save() {
	ds_list2.ClearData();
	ds_list_d.ClearData();
    ds_list_d2.ClearData();
    ds_list_d3.ClearData();
    ds_list_d4.ClearData();
	ds_sms.ClearData();
	ds_sms_h.ClearData();


	var rows = 0; //ds_list2 index
	ds_list_i.ClearData();
	var nRow = 0; //ds_list_i index
	for(var i=0;i<ds_list.GetRowCount();i++){
		//if(ds_list.GetColumn(i,"PARQDT") == ds_list.GetOrgColumn(i,"PARQDT"))
		//{
		//	ds_list.SetColumn(i,"EXAMREQUEST","0");
		//}
		if(ds_list.GetColumn(i,"EXAMREQUEST")=="1"){
			if(ds_list.GetColumn(i,"EXAMTYPE")==""){
				//alert("검사유형을 선택하십시오.");
				gfn_showAlert("CW00001","검사유형");
				return false;
			}
			if(Length(Trim(ds_list.GetColumn(i,"PARQDT")))!=8){
				//alert("검사요청일을 입력하십시오.");
				gfn_showAlert("CW00001","검사요청일");
				return false;
			}
		}

//trace(ds_list.SaveXML());			
//alert(ds_list.GetOrgColumn(i,"PARQDT"));
//alert(ds_list.GetColumn(i,"PARQDT"));		
//alert(ds_list.GetColumn(i,"PACFDT"));		
//alert(ds_list.GetColumn(i,"EXAMREQUEST"));		
//alert(ds_list.GetColumn(i,"CHG_TEAM"));	
		//==========================변경팀 추가===================================
		if(ds_list.GetColumn(i,"PARQDT") != ds_list.GetOrgColumn(i,"PARQDT") &&
		   ds_list.GetColumn(i,"PACFDT") > '00000000' && ds_list.GetColumn(i,"EXAMREQUEST") == "1" && 
		   ds_list.GetColumn(i,"CHG_TEAM") == ""   )
		{		
//alert("ok");		
			gfn_showAlert("CW00001","변경팀");
			return false;		
		}
		//========================================================================


		if(ds_list.GetColumn(i,"EXAMREQUEST") == "1") ds_list.SetColumn(i,"FLAG","U"); 

		if(ds_list.GetColumn(i,"PARQDT") != ds_list.GetOrgColumn(i,"PARQDT") &&
		   ds_list.GetColumn(i,"PACFDT") > '00000000' && ds_list.GetColumn(i,"EXAMREQUEST") == "1")
		{
			ds_list2.AddRow();
			ds_list2.SetColumn(rows,"WERKS",ds_list.GetColumn(i,"WERKS"));
			ds_list2.SetColumn(rows,"PSPID",ds_list.GetColumn(i,"PSPID"));
			ds_list2.SetColumn(rows,"ART",ds_list.GetColumn(i,"ART"));
			ds_list2.SetColumn(rows,"HOGI",ds_list.GetColumn(i,"HOGI"));
			ds_list2.SetColumn(rows,"SEQ",ds_list.GetColumn(i,"SEQ"));
			ds_list2.SetColumn(rows,"PARQDT",ds_list.GetOrgColumn(i,"PARQDT"));
			ds_list2.SetColumn(rows,"PARQDT2",ds_list.GetColumn(i,"PARQDT"));
			ds_list2.SetColumn(rows,"PACFDT",ds_list.GetColumn(i,"PACFDT"));
			ds_list2.SetColumn(rows,"EXEMPTION",ds_list.GetColumn(i,"EXEMPTION"));
			ds_list2.SetColumn(rows,"REQDT",ds_list.GetColumn(i,"REQDT"));
			ds_list2.SetColumn(rows,"REQTM",ds_list.GetColumn(i,"REQTM"));
			ds_list2.SetColumn(rows,"FLAG","I");
			ds_list2.SetColumn(rows,"DELCHK","");
			//추가//
			ds_list2.SetColumn(rows,"CHG_TEAM",ds_list.GetColumn(i,"CHG_TEAM"));

			//추가 - 변경불가
			ds_list2.SetColumn(rows,"DIS_YN",ds_list.GetColumn(i,"DIS_YN"));

			//====//			
			rows++;
		}

		if(ds_list.getColumn(i, "ART") == '8950' && ds_list.GetColumn(i,"FNAME") != ds_list.GetOrgColumn(i,"FNAME"))
		{
			ds_list_i.AddRow();
			ds_list_i.SetColumn(nRow,"PJT",ds_list.GetColumn(i,"PSPID"));
			ds_list_i.SetColumn(nRow,"HNO",ds_list.GetColumn(i,"HOGI"));
			ds_list_i.SetColumn(nRow,"CHASU",ds_list.GetColumn(i,"SEQ"));
			ds_list_i.SetColumn(nRow,"FNAME",ds_list.GetColumn(i,"FNAME"));
			ds_list_i.SetColumn(nRow,"DEL_FNAME",ds_list.GetColumn(i,"DEL_FNAME"));
			ds_list_i.SetColumn(nRow,"FPATH",ds_list.GetColumn(i,"FPATH"));
			ds_list_i.SetColumn(nRow,"FNAME_APP",ds_list.GetColumn(i,"FNAME_APP"));
			ds_list_i.SetColumn(nRow,"FNAME_PATH",ds_list.GetColumn(i,"FNAME_PATH"));
			nRow++;
		}

		if(ds_list.GetColumn(i,"EXAMREQUEST") == "1"  && ( ds_list.GetColumn(i,"PARQDT") <>  ds_list.GetColumn(i,"PACFDT3") ) ){
		 if(ds_exam2.GetRowCount()==0)	ds_exam2.AddRow();

		//대수확인	
           if( ds_list.GetColumn(i,"ZZACTSS") == ''|| ds_list.GetColumn(i,"ZZACTSS") == '01'|| ds_list.GetColumn(i,"ZZACTSS") == '02' || ds_list.GetColumn(i,"ZZACTSS") == '08' || ds_list.GetColumn(i,"ZZACTSS") == '10' 
                ||  ds_list.GetColumn(i,"ZZACTSS") == '14' || ds_list.GetColumn(i,"ZZACTSS") == '20'  ||  ds_list.GetColumn(i,"ZZACTSS") == '21' ||  ds_list.GetColumn(i,"ZZACTSS") == '19')
             {  
                ds_exam.SetColumn(0,"ZZACTSS", '22')  ; //수도권으로 사무소 변경
                ds_exam2.SetColumn(0,"ZZACTSS", '22')  ; //수도권으로 사무소 변경
             }
              else if (ds_list.GetColumn(0,"ZZACTSS") == '15' || ds_list.GetColumn(i,"ZZACTSS") == '16' )
             {  
                ds_exam.SetColumn(0,"ZZACTSS", '23')  ; //주차로 사무소  변경
                ds_exam2.SetColumn(0,"ZZACTSS", '23')  ; //주차로 사무소  변경
             }
              else
             {
                ds_exam.SetColumn(0,"ZZACTSS", ds_list.GetColumn(i,"ZZACTSS"))  ; 
                ds_exam2.SetColumn(0,"ZZACTSS", ds_list.GetColumn(i,"ZZACTSS"))  ; 
             }

               ds_exam.SetColumn(0,"EXAMREQDATE",ds_list.GetColumn(i,"PARQDT"));
               ds_exam2.SetColumn(0,"EXAMREQDATE",ds_list.GetColumn(i,"PARQDT3"));

                tit_clearActionInfo();
		        tit_addSearchActionInfo("qm05:QM0501001A_S11");
		        tit_addSearchActionInfo("qm05:QM0501001A_S13");

		        tit_callService(
			     ""
			   , ""
			   , "ds_exam=ds_exam ds_exam2=ds_exam2"
			   , "ds_list_d=ds_list_d ds_list_d3=ds_list_d3"
			   , ""
			   , ""//"ufn_afterdaesuCount"
			   , ""
               , ""
			   , true);	


              if(ds_list_d2.GetRowCount()==0)	
               {//alert("a");
	             ds_list_d2.copyF(ds_list_d);
                 ds_list_d2.SetColumn(0,"DAESU_H2", ds_list_d.GetColumn(0,"DAESU_H") + ds_list.GetColumn(i, "DAESU"));
                // alert(ds_list_d.GetColumn(0,"DAESU_T"));
                //  alert(ds_list_d2.GetColumn(0,"DAESU_H2"));
                 if(ds_list_d.GetColumn(0,"DAESU_T") < ds_list_d2.GetColumn(0,"DAESU_H2")) //  총대수보다 현 신청대수가 클 경우 
                 {//alert("C");
                    var txt = (parseInt(j) + 1) + "행 검사요청일 신청대수가 초과하였습니다.다른 일자를";
  	                gfn_showAlert("CW00116", txt);
		            return false;
                 }
               }
                else
                {
                 for(var j=0;j<ds_list_d2.GetRowCount();j++)
                 {
                  if( (ds_list_d.GetColumn(0,"MDATE") == ds_list_d2.GetColumn(j,"MDATE")) && (ds_list_d.GetColumn(0,"ZZACTSS") == ds_list_d2.GetColumn(j,"ZZACTSS")) )
                   {//alert("w");
                     ds_list_d2.SetColumn(j,"DAESU_H2", ds_list_d2.GetColumn(j,"DAESU_H2") + ds_list.GetColumn(i, "DAESU"));
                     ds_list_d.ClearData();
                     if(ds_list_d2.GetColumn(j,"DAESU_H2") > ds_list_d2.GetColumn(j,"DAESU_T")) //  총대수보다 현 신청대수가 클 경우 
                     {//alert("f");
                        var txt = (parseInt(j) + 1) + "행 검사요청일 신청대수가 초과하였습니다. 다른 일자를 ";
  	                    gfn_showAlert("CW00116", txt);
	           //  return;
		                 return false;
                      }
                    }
                    else
                    {//alert("d");

                      ds_list_d.SetColumn(0,"DAESU_H2", ds_list_d.GetColumn(0,"DAESU_H") + ds_list.GetColumn(i, "DAESU"));

                      if(ds_list_d.GetColumn(0,"DAESU_T") < ds_list_d.GetColumn(0,"DAESU_H2")) //  총대수보다 현 신청대수가 클 경우 
                      {//alert("x");

                        var txt = (parseInt(j) + 1) + "행 검사요청일 신청대수가 초과하였습니다.다른 일자를";
  	                    gfn_showAlert("CW00116", txt);

		                 return false;
                       }

                     }

                   }

                   ds_list_d2.AppendDataset(ds_list_d);
               }

                 if(ds_list_d3.GetRowCount() >= 1 ){ //alert("a");
			      if(ds_list_d4.GetRowCount() == 0)
			      { //alert("b");
			        if(ds_list.GetColumn(i,"G_CHK") == '1')
			        {
			          ds_list_d3.SetColumn(0,"DAESU_H", ds_list_d3.GetColumn(0, "DAESU_H") - ds_list.GetColumn(i, "DAESU2") );
			        }
			        else
			        {
			          ds_list_d3.SetColumn(0,"DAESU_H", ds_list_d3.GetColumn(0, "DAESU_H") - ds_list.GetColumn(i, "DAESU") );
			        }

        	        ds_list_d4.copyF(ds_list_d3);
        	      }
			      else{  //alert("b");
			        for(var j=0;j<ds_list_d4.GetRowCount();j++)
                     { //alert("c");
                       if( (ds_list_d3.GetColumn(0,"MDATE") == ds_list_d4.GetColumn(j,"MDATE")) && (ds_list_d3.GetColumn(0,"ZZACTSS") == ds_list_d4.GetColumn(j,"ZZACTSS")) )
                       {//alert("w");
                         if(ds_list.GetColumn(i,"G_CHK") == '1')
                         {
        	                ds_list_d4.SetColumn(j,"DAESU_H", ds_list_d4.GetColumn(j, "DAESU_H") - ds_list.GetColumn(i, "DAESU2") );
        	             }
        	             else
        	             {
        	               ds_list_d4.SetColumn(j,"DAESU_H", ds_list_d4.GetColumn(j, "DAESU_H") - ds_list.GetColumn(i, "DAESU") );
        	             }
                        ds_list_d3.ClearData();
                       }
                        else
                        {//alert("d");
                          if(ds_list.GetColumn(i,"G_CHK") == '1'){
        	                 ds_list_d3.SetColumn(0,"DAESU_H", ds_list_d3.GetColumn(0, "DAESU_H") - ds_list.GetColumn(i, "DAESU2") );
        	              }else{
        	                 ds_list_d3.SetColumn(0,"DAESU_H", ds_list_d3.GetColumn(0, "DAESU_H") - ds_list.GetColumn(i, "DAESU") );
        	              }
                        }

                       }

                      ds_list_d4.AppendDataset(ds_list_d3);
                  }

			    }



			 }


			 //trace(ds_list_d2.SaveXML());
	}

	var fileName = ""; //file서버에 이미지 저장
	for(var i = 0; i < ds_list_i.rowcount; i++)
	{
		if(length(ds_list_i.getColumn(i, "FNAME")) != 0 && length(ds_list_i.getColumn(i, "FNAME_PATH")) != 0)
		{
			//fileName = left(GetDate(),8)+"_"+right(GetDate(),6)+"_"+ds_list_i.getColumn(i, "HNO")+ds_list_i.getColumn(i, "CHASU")+"."+gfn_getFileExt(ds_list_i.getColumn(i, "FNAME"));
			fileName = left(GetDate(),8)+"_"+right(GetDate(),6)+"_"+ds_list_i.getColumn(i, "FNAME");//+"."+gfn_getFileExt(ds_list.getColumn(i, "FNAME"));
			var arr = tit_fileWrite(httpFile
				, "QM"
				, ds_list_i.getColumn(i, "FNAME_PATH")
				, ds_list_i.getColumn(i, "FNAME")
				, fileName// 실제 db에 저장될 파일 이름
				, "U"	  // time stamp를 붙이지 않을 경우 U 사용 
				, ds_list_i.GetColumn(i,"DEL_FNAME"));    // ds_list_i.getColumn(i, "DEL_FNAME") );//삭제할 이전 파일명 
			if ( arr[0] == "S" ) {
				ds_list_i.setColumn(i, "FNAME_APP", arr[2]);
				ds_list_i.setColumn(i, "FPATH", ds_list_i.getColumn(i, "FNAME_PATH"));
			}
			else if ( arr[0] != "S" )
			{
				gfn_showMsg("CW00035"); 
				return;
			}
		}
	}


	// 검사요청일 변경 시 SMS자동발송
	for(var i=0;i<ds_list.GetRowCount();i++){
		if(ds_list.GetColumn(i,"PARQDT") != ds_list.GetOrgColumn(i,"PARQDT") &&
			ds_list.GetColumn(i,"EXAMREQUEST") == "1")
		{
			var nSmsRow = fn_sms(i);

			var nAddRow = ds_sms_h.AddRow();
			ds_sms_h.SetColumn(nAddRow,"WERKS",ds_list.GetColumn(i,"WERKS"));
			ds_sms_h.SetColumn(nAddRow,"PSPID",ds_list.GetColumn(i,"PSPID"));
			ds_sms_h.SetColumn(nAddRow,"ART",ds_list.GetColumn(i,"ART"));
			ds_sms_h.SetColumn(nAddRow,"HOGI",ds_list.GetColumn(i,"HOGI"));
			ds_sms_h.SetColumn(nAddRow,"SEQ",ds_list.GetColumn(i,"SEQ"));

			ds_sms_h.SetColumn(nAddRow,"CELLP",ds_sms.GetColumn(nSmsRow,"TRAN_PHONE"));
			ds_sms_h.SetColumn(nAddRow,"ZSITE_NM",ds_list.GetColumn(i,"POST1"));
			ds_sms_h.SetColumn(nAddRow,"LIFNR",ds_list.GetColumn(i,"LIFNR"));
			ds_sms_h.SetColumn(nAddRow,"LIFNRNM",ds_list.GetColumn(i,"LIFNRNM"));
			ds_sms_h.SetColumn(nAddRow,"TEMNO",ds_list.GetColumn(i,"TEMNO"));
			ds_sms_h.SetColumn(nAddRow,"NAMET",ds_list.GetColumn(i,"TEMNONM"));
			ds_sms_h.SetColumn(nAddRow,"FR_PARQDT",ds_list.GetOrgColumn(i,"PARQDT"));
			ds_sms_h.SetColumn(nAddRow,"TO_PARQDT",ds_list.GetColumn(i,"PARQDT"));

			//trace(ds_sms_h.saveXML());

		}

	}

	tit_clearActionInfo();
	tit_addSaveActionInfo(
		""
		, "qm05:QM0501002A_U1"
		, ""
		, "FLAG"
		, ""
		, -1
		, "");	

	if(ds_list2.rowcount > 0)
	{
		tit_addSaveActionInfo(
		"qm05:QM0501002A_I1"
		, ""
		, ""
		, "FLAG"
		, ""
		, -1
		, "");	
	}

	tit_addSelectExActionInfo("qm05:QM0501001A_S8", "qm05:QM0501001A_I2", "qm05:QM0501001A_U1");

	// 저장 처리 
	tit_callService(
        ""
        , ""
        , "ds_list=ds_list:A ds_list2=ds_list2:A ds_list_i=ds_list_i"
        , ""
        , ""
        , "fn_afterSave");

    // 문자발송
    tit_clearActionInfo();
	tit_addMultiActionInfo("ps07:PS0704001A_I2");
	tit_callService(
		"PDA_ACT"
		, ""
		, "ds_sms=ds_sms"
		, ""
		, ""
		, "fn_smsSend" //""
		, ""
		, ""
		, true);

}
