/********************************************************************************
* 기      능   : sms 전송 전 양식 만들기
********************************************************************************/
function fn_sms(nRow) 
{
	var lv_msg = "";
    var lv_tel = ""; //"010-4714-5711";

    var sCellp   = ds_list.GetColumn(nRow, "CELLP");	// H.P번호
    var sHogi    = ds_list.GetColumn(nRow, "HOGI");		// 호기
    var sPost1   = ds_list.GetColumn(nRow, "POST1");	// 현장명
    var sLifnrNm = ds_list.GetColumn(nRow, "LIFNRNM") + " " + ds_list.GetColumn(nRow, "TEMNONM");	// 업체명
    var sNewDt   = ds_list.GetColumn(nRow, "PARQDT");	// 변경후
    var sOldDt   = ds_list.GetOrgColumn(nRow, "PARQDT");	// 변경전

    if(length(sPost1) > 15)	// 현장명 15자리가 넘어가면 15자리로 맞추도록 함.
    {
		sPost1 = substr(sPost1,0,15);
    }

    sNewDt = substr(sNewDt,0,4) + ". " + substr(sNewDt,4,2) + ". " + substr(sNewDt,6,2) + "일";
    sOldDt = substr(sOldDt,0,4) + ". " + substr(sOldDt,4,2) + ". " + substr(sOldDt,6,2) + "일";

    sCellp = "01071601959";

	if(length(sCellp) == 11)
	{
		sCellp = substr(sCellp,0,3) + "-" + substr(sCellp,3,4) + "-" + substr(sCellp,7,4);
	}
	else if(length(sCellp) == 10)
	{
		sCellp = substr(sCellp,0,3) + "-" + substr(sCellp,3,3) + "-" + substr(sCellp,6,4);
	}
	else
	{
		return;
	}

    /*
    --> 문자 양식
    [Web발신]
	검사물량 대수 초과로 인한 검사일정 연기 안내
	프로젝트NO. 166355L01
	현장명 : 수원 영통 아이파크캐슬 2단지
	업체명 : (주)송주엘리베이터 박상철
	변경전 : 2018. 00. 00일
	변경후 : 2018. 00. 00일
    */


	lv_msg = '[Web발신]' + '\n검사물량 대수 초과로 인한 검사일정 연기 안내'+ '\n프로젝트NO.  ' + sHogi + '\n현장명 : ' + sPost1 + '\n업체명 : ' + sLifnrNm + '\n변경전 : ' + sOldDt + '\n변경후 : ' + sNewDt;

	lv_tel = sCellp;

	var nAddRow = ds_sms.AddRow();
	//gfn_initDs(ds_sms, true);
	//ds_sms.SetColumn(nAddRow,"TRAN_ETC1", "PS1204008B");
	//ds_sms.SetColumn(0,"TRAN_TYPE", "1");
	//ds_sms.SetColumn(0,"TRAN_STATUS", "1");
	ds_sms.SetColumn(nAddRow,"TRAN_ETC1", "QM0501002A");
	ds_sms.SetColumn(nAddRow,"TRAN_TYPE", "0");
	ds_sms.SetColumn(nAddRow,"TRAN_STATUS", "0");
	ds_sms.SetColumn(nAddRow,"TRAN_CALLBACK", "1577-0603");
	ds_sms.SetColumn(nAddRow,"TRAN_MSG", lv_msg);
    ds_sms.SetColumn(nAddRow,"TRAN_PHONE", lv_tel);

	return nAddRow;
}

/********************************************************************************
* 기      능   : sms 메시지 Callback  
********************************************************************************/
function fn_smsSend(errCode, errMsg) 
{
	if ( errCode != 0 ) 
	{
		global.FRM_BOTTOM.img_msgType.imageId = "bottom_icon_caution";
		global.FRM_BOTTOM.st_msg.color = "yellow";       
		global.FRM_BOTTOM.st_msg.value = "SMS발송을 실패하였습니다.";
		return;
	}
	else
	{
		// 문자발송 후 내역 저장.
		tit_clearActionInfo();

		tit_addMultiActionInfo("qm05:QM0501002A_I2");
		tit_callService(
			  ""	
			, ""
			, "ds_sms_h=ds_sms_h"
			, ""
			, ""
			, ""//"fn_afterSave"
			, ""
			, ""
			, true  // ""
		);
	}

}

