/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
function fn_query() {

    if(userGroup == '10' && cb_zzactss.Value == '' && cb_zzlifnr.Value == '' && chk_s.Value == '')
    {
        gfn_showMsg("CW10025","사무소 또는 협력업체를 선택해주세요.");
		 return;
    }
	if(ufn_checkQuery()){
		var strCheck="0000";	
		if(ds_cond.GetColumn(0,"CHECK1")=="1"){
			strCheck = "1"+SubStr(strCheck,1,3);
		}
		if(ds_cond.GetColumn(0,"CHECK2")=="1"){
			strCheck = SubStr(strCheck,0,1)+"1"+SubStr(strCheck,2,2);
		}
		if(ds_cond.GetColumn(0,"CHECK3")=="1"){
			strCheck = SubStr(strCheck,0,2)+"1"+SubStr(strCheck,3,1);
		}	
		if(ds_cond.GetColumn(0,"CHECK4")=="1"){
			strCheck = SubStr(strCheck,0,3)+"1";
		}				

		//------------김대식사원요청(주차추가) --------------------------//
		if(cb_zzactss.Value == '13' || cb_zzactss.Value == '15' ) ds_cond.SetColumn(0,"GBN","Y");
		else ds_cond.SetColumn(0,"GBN","N");

	   if(rd_jpjt.Value = '1') ds_cond.SetColumn(0,"JPJT","B");
       else  ds_cond.SetColumn(0,"JPJT","A");
       if(chk_s.Value = '1') ds_cond.SetColumn(0,"SUDO",1);
       else  ds_cond.SetColumn(0,"SUDO",0);

	   ds_cond.SetColumn(0,"CHECK",strCheck);

		//	 권한 별 조회위해 
    	ds_cond.SetColumn(0,"USERGROUP", userGroup);

		tit_clearActionInfo();
		tit_addSearchActionInfo("qm05:QM0501005A_S1");
		tit_callService(
			""
			, ""
			, "ds_cond=ds_cond"
			, "ds_list=ds_list"
			, ""
			, "fn_afterQuery"
			, true);

	}
}
function ufn_checkQuery(){
	if(ds_cond.GetColumn(0,"FPJT") > ds_cond.GetColumn(0,"TPJT")){
		//alert("Pjt No From,To 를 확인하세요. ");
		gfn_showAlert("CW00022","Pjt No From,To");
		return false;		
	}
	if ( !gfn_checkFromToObj(cal_datef, cal_datet, true, st_date.value) ) {
		return;
	}
	return true;
}
function fn_initQuery() {

	tit_clearActionInfo();
	tit_addSearchActionInfo("qm05:QM0501001A_S1");
	tit_addSearchActionInfo("ps02:PS0203001A_S1");
	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_team=ds_team ds_zzlifnr=ds_zzlifnr"
        , ""
        , "fn_afterInitQuery"
        , true);
}
function fn_afterInitQuery(){
	if(userGroup == '10')
	{  //alert(G_AREA_CODE_E);
		ds_zzlifnr.Filter("CODE=="+quote(G_VEND_CODE_E));
	//	gfn_insertEmptyRow( ds_zzlifnr, G_CODE_ALL );
		//ds_zzlifnr.Filter("ZZACTSS=="+quote(G_AREA_CODE_E)+" || CODE==''");
		cb_zzlifnr.Index = 0;
		cb_zzlifnr.Enable = true;
		gfn_insertEmptyRow( ds_zzlifnr, G_CODE_ALL );
		ds_team.Filter("ZZLIFNR=="+quote(G_VEND_CODE_E)+" || CODE==''");
		cb_team.Index = 0;
		cb_team.Enable = true;
		if(G_AREA_CODE_E == '01' || G_AREA_CODE_E== '02'|| G_AREA_CODE_E== '08'||  G_AREA_CODE_E== '10' || G_AREA_CODE_E ==  '14' 
		    || G_AREA_CODE_E == '20'|| ZZACTSS== '21' || ZZACTSS== '19')
		{
		  chk_s.Enable = true;
		}
		else
		{
		 chk_s.Enable = false;
		}
    }
	else if(userGroup == '30')
	{
		gfn_insertEmptyRow( ds_zzlifnr, G_CODE_ALL );
		ds_zzlifnr.Filter("ZZACTSS=="+quote(G_AREA_CODE_E)+" || CODE==''");
		cb_zzlifnr.Index = 0;
		cb_zzlifnr.Enable = true;
	}
	else
	{
		gfn_insertEmptyRow( ds_zzlifnr, G_CODE_ALL );
		cb_zzlifnr.Index = 0;
		cb_zzlifnr.Enable = true;
	}
	gfn_insertEmptyRow( ds_team, G_CODE_ALL );
	cb_team.Index = 0;

	//ds_cond.SetColumn(0,"TEAM","");
	ds_cond.SetColumn(0,"RESULT","");

}
