/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQueryC(errCode, errMsg) {
	if(ds_outCheck.GetRowCount()==0){
		//alert("검사요청일을 수정하실수 없습니다.");
		gfn_showAlert("CW00107","변경검사요청일");
		ds_list.FireEvent=false;
		ds_list.SetColumn(ds_list.row,"PAPRDT_F",ds_list.GetOrgColumn(ds_list.row,"PAPRDT_F"));
		ds_list.SetColumn(ds_list.currow,"EXAMREQUEST","0");
		ds_list.FireEvent=true;	
	}
	else{
		var x= ToNumber(ds_list.getColumn(ds_list.currow,"PACFDT"))-ToNumber(ds_outCheck.GetColumn(0,"POSSDT"));
		if(x<=ToNumber(gfn_getCurrDate())){
			//alert("검사요청일을 수정하실수 없습니다.");
			gfn_showAlert("CW00107","변경검사요청일");
			ds_list.FireEvent=false;
			ds_list.SetColumn(ds_list.row,"PAPRDT_F",ds_list.GetOrgColumn(ds_list.row,"PAPRDT_F"));
			ds_list.SetColumn(ds_list.currow,"EXAMREQUEST","0");
			ds_list.FireEvent=true;				
		}
		else{
			ds_list.FireEvent=false;
			ds_list.SetColumn(ds_list.row,"EXAMREQUEST","1");
			ds_list.FireEvent=true;	
		}
	}

}
/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave(errCode, errMsg) {

	  if(ds_list_d2.GetRowCount() > 0){

