		, ""
		, ""
		, ""//"fn_afterSave"
		, ""
		, ""
		, true  // ""
    );

 }
    //sms 전송처리 
   for(var j=0;j<ds_list_c.GetRowCount();j++)
   {
     if(length(ds_list_c.GetColumn(j,"CELLP")) > 0 )
     {
        var lv_msg = "";
	   // var tell = "";
	    var tell = ds_list_c.GetColumn(j,"CELLP");

