	         + '③최초신청일:  '+  ds_list_c.GetColumn(j,"PARQDT")+ ' ④검사확정일:  '+  ds_list_c.GetColumn(j,"PAPRDT_F");

	   gfn_initDs(ds_sms, true);

   	  ds_sms.SetColumn(0,"TRAN_ETC1", "QM051005A");
      ds_sms.SetColumn(0,"TRAN_TYPE", "0");
	  ds_sms.SetColumn(0,"TRAN_STATUS", "0");
	  ds_sms.SetColumn(0,"TRAN_CALLBACK", "032-644-5691");
	  ds_sms.SetColumn(0,"TRAN_MSG", lv_msg);
	  ds_sms.SetColumn(0,"TRAN_PHONE",tell);	    

//trace(ds_sms.SaveXML()) ;

     tit_clearActionInfo();
     	tit_addSingleActionInfo("qm05:QM0501005A_I1");

	    tit_callService(
	   	 "PDA_ACT"
	    , ""
		, "ds_sms=ds_sms"
		, ""
		, ""
		, ""
		, ""
		, ""
		, true);

     }


   }



	fn_query();

}
