/********************************************************************************
* 기      능   : 그리드 헤더 클릭시 정렬 처리 
********************************************************************************/
function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 0 ) {
		// 그리드 표시할 컬럼 선택 
		gfn_selectGridCol(obj);
	} else {
		// 그리드 정렬 처리 
		gfn_sortGrid(obj,nCell);
	}
}
/********************************************************************************
* 기      능   : 데이터셋 값 변경 시 - Flag 처리 
********************************************************************************/
function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID=="PAPRDT_F"){
		if(ToNumber(varNewValue) < ToNumber(gfn_getCurrDate()) || ToNumber(varNewValue) < ToNumber(ds_list.GetColumn(nRow,"PACFDT"))){
			//alert("검사요청일을 확인하십시오.");
			gfn_showAlert("CW00022","변경검사요청일");
			ds_list.FireEvent=false;
			ds_list.SetColumn(nRow,strColumnID,varOldValue);
			ds_list.SetColumn(nRow,"EXAMREQUEST","0");			
			ds_list.FireEvent=true;			
		}
		else if(ds_list.GetColumn(nRow,"SV_STUS")=="4" || ds_list.GetColumn(nRow,"SV_STUS")=="5"){			
			//alert("검사요청일을 수정하실수 없습니다.");
			gfn_showAlert("CW00022","변경검사요청일");
			ds_list.FireEvent=false;
			ds_list.SetColumn(nRow,strColumnID,varOldValue);
			ds_list.SetColumn(nRow,"EXAMREQUEST","0");	
			ds_list.FireEvent=true;				
		}
		else if(ds_list.GetColumn(nRow,"SV_STUS")=="3"){
		 //체크
			ufn_checkModify(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex);
		}

		//추가 (배정이 되어 있으면 날짜 수정 불가:맹준호씨 요청(2012.03.28)
		else if(ds_list.GetColumn(nRow,"PACFDT") > '00000000'){
			gfn_showAlert("CW00022","변경검사요청일");
			ds_list.FireEvent=false;
			ds_list.SetColumn(nRow,strColumnID,varOldValue);
			ds_list.SetColumn(nRow,"EXAMREQUEST","0");			
			ds_list.FireEvent=true;			
		}
	// 면제 기간이 아닐 시 
	   else if(ds_list.getcolumn(nRow,"EXEMPTION") == 'O' && ds_list.getcolumn(nRow,"PASS") == 'X' && (ToNumber(varNewValue) <  ToNumber(ds_list.GetColumn(nRow,"S_DATE"))  || ToNumber(varNewValue) > ToNumber(ds_list.GetColumn(nRow,"F_DATE"))))
	    {   

			  gfn_showAlert("면제대상이 아닙니다.");
			  ds_list.FireEvent=false;
			  ds_list.SetColumn(nRow,strColumnID,varOldValue);
			  ds_list.SetColumn(nRow,"EXAMREQUEST","0");			
			  ds_list.FireEvent=true;			
	    }



		else{
			ds_list.FireEvent=false;
			ds_list.SetColumn(nRow,"EXAMREQUEST","1");
			ds_list.FireEvent=true;			
		}
   		  ufn_setexamCond();
		  ufn_chkdate();
	}
	else if(strColumnID=="EXAMREQUEST"){
		//if(ds_list.GetColumn(nRow,"PACFDT") > '00000000' || ds_list.GetColumn(nRow,"SV_STUS")=="3" || ds_list.GetColumn(nRow,"SV_STUS")=="4" || ds_list.GetColumn(nRow,"SV_STUS")=="5"){
		if(ds_list.GetColumn(nRow,"SV_STUS")=="3" || ds_list.GetColumn(nRow,"SV_STUS")=="4" || ds_list.GetColumn(nRow,"SV_STUS")=="5"){
			//alert("수정 혹은 삭제가 불가합니다.");
			gfn_showAlert("CW00114","수정 혹은 삭제가");
			ds_list.FireEvent=false;
			ds_list.SetColumn(nRow,"EXAMREQUEST","0");	
			ds_list.FireEvent=true;			
		}
	}
    // 관리자권한 
	if(strColumnID == "G_CHK"){
	  if(varNewValue == '1')
	  {
	    ds_list.SetColumn(ds_list.currow,"DAESU",0.0);	
	  }
	 else
	  {
	    ds_list.SetColumn(ds_list.currow,"DAESU", ds_list.GetColumn(ds_list.currow,"DAESU2"));	
	  }
	 }


}
// 달력 처리 부분 
/********************************************************************************
* 기      능   : 그리드에서 날짜 입력시 
********************************************************************************/
function grd_list_OnExpandEdit(obj,nRow,nCell,strVal,nPivotIndex)
{
	if ( nCell == 15 ) {
		var rect = obj.GetCellRect(nRow, ncell);

		cal_temp.top = rect[1] + 120;
		if (cal_temp.top > 400)
		{
			cal_temp.top = 400;
		}
		cal_temp.Left = rect[0] + 20; 
		if (cal_temp.Left > 790)
		{
			cal_temp.Left = 790;
		}	 
		cal_temp.Visible = true;


		cal_temp.value = ds_list.getColumn(nRow, "PAPRDT_F");

		cal_temp.SetFocus();
	}	

}
/********************************************************************************
* 기      능   : 달력에서 일자 선택했을 경우 
********************************************************************************/
function cal_temp_OnDayClick(obj,strText)
{
	if(grd_list.GetClickedCell() == 15)
		ds_list.SetColumn(ds_list.row, "PAPRDT_F", strText);	

	obj.Visible = false;
}
/********************************************************************************
* 기      능   : 달력에서 포커스 잃었을 경우 
********************************************************************************/
function cal_temp_OnKillFocus(obj)
{
	obj.Visible = false;
}
/********************************************************************************
* 기      능   : 원프로젝트 팝업
********************************************************************************/
function btn_pjt_OnClick(obj)
{
	var strId = obj.id;
	var strPjt;
	if(strId=="btn_fPjt") strPjt = ed_fPjt.Text;
	else if(strId=="btn_tPjt") strPjt = ed_tPjt.Text;
	var arg = "pjt="+quote(strPjt)+" strId="+quote(strId);
	gfn_openPopup("CS9601002A",true,arg);
}
/********************************************************************************
* 기      능   : 사무소 변경시 협력업체 FILTERING
********************************************************************************/
function cb_zzactss_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{   
	if(strCode == '') ds_zzlifnr.Filter("1==1");
	else ds_zzlifnr.Filter("ZZACTSS=="+quote(strCode)+" || CODE==''");
	cb_zzlifnr.Index = 0;
	//설치팀 dataset초기화
	ds_team.Filter("1==1");
	cb_team.Index = 0;
}
/********************************************************************************
* 기      능   : 협력업체 변경시 팀장 FILTERING
********************************************************************************/
function cb_zzlifnr_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{   
	if(strCode == '') ds_team.Filter("1==1");
	else ds_team.Filter("ZZLIFNR=="+quote(strCode)+" || CODE==''");
	cb_team.Index = 0;
}

