********************************************************************************/
function Checkbox2_OnClick(obj,strValue)
{  //alert(chk_s.Value);
	if(userGroup == '10')
	{
  	 if(chk_s.Value == '1') //수도권 체크 
	 {//수도권  '01','02','08','10','14','20','21'
	  // gfn_insertEmptyRow( ds_zzactss, G_CODE_ALL );
	   cb_zzactss.Index = 0;
       ds_zzlifnr.Filter("ZZACTSS== '01' || ZZACTSS== '02'|| ZZACTSS== '08'||  ZZACTSS== '10' || ZZACTSS== '14' || ZZACTSS== '20'|| ZZACTSS== '21'|| ZZACTSS== '19' || CODE==''");

	 }
	 else
	 { //alert(G_AREA_CODE_E);
	    cb_zzactss.Index =  ds_zzactss.SearchRow("CODE=="+quote(G_AREA_CODE_E));
	   // ds_zzlifnr.Filter("ZZACTSS=="+quote(G_AREA_CODE_E)+" || CODE==''");
		ds_zzlifnr.Filter("CODE=="+quote(G_VEND_CODE_E) + " || ZZACTSS=="+quote(G_AREA_CODE_E)+" || CODE==''" );
		cb_zzlifnr.Index = 0;
	//	gfn_insertEmptyRow( ds_zzlifnr, G_CODE_ALL );
	 }

   }
   else if(userGroup == '40')
   {
      if(chk_s.Value == '1') //수도권 체크 
	 {//수도권  '01','02','08','10','14','20','21'
	   cb_zzlifnr.Index = 0;
	   cb_zzactss.Index = 0;
       ds_zzlifnr.Filter("ZZACTSS== '01' || ZZACTSS== '02'|| ZZACTSS== '08'||  ZZACTSS== '10' || ZZACTSS== '14' || ZZACTSS== '20'|| ZZACTSS== '21' || ZZACTSS== '19'|| CODE==''");

	 }
	 else
	 {
		cb_zzlifnr.Index = 0;
	    ds_zzlifnr.UnFilter();
	 }

   }
}

