/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
function fn_query() {
	// 인계일자
	if ( !gfn_checkFromToObj(cal_parqdt_f, cal_parqdt_t, true, st_parqdt.value, 0, true) ) {
		return;
	}

	if(ed_pjt.value != "")
		ds_cond.SetColumn(0,"GBN","A");
	else if(ed_pjt.value == "")
		ds_cond.SetColumn(0,"GBN","B");

	var strCheck="0000";	
	if(ds_cond.GetColumn(0,"CHECK1")=="1"){
		strCheck = "1"+SubStr(strCheck,1,3);
	}
	if(ds_cond.GetColumn(0,"CHECK2")=="1"){
		strCheck = SubStr(strCheck,0,1)+"1"+SubStr(strCheck,2,2);
	}
	if(ds_cond.GetColumn(0,"CHECK3")=="1"){
		strCheck = SubStr(strCheck,0,2)+"1"+SubStr(strCheck,3,1);
	}	
	if(ds_cond.GetColumn(0,"CHECK4")=="1"){
		strCheck = SubStr(strCheck,0,3)+"1";
	}				
	ds_cond.SetColumn(0,"CHECK",strCheck);

	if(user_grp == "10") ds_cond.SetColumn(0,"GUBUN","A");
	else ds_cond.SetColumn(0,"GUBUN","B");

	tit_clearActionInfo();
	tit_addSearchActionInfo("qm05:QM0502002A_S1");

	tit_callService(
		""
		, ""
		, "ds_cond=ds_cond"
		, "ds_list=ds_list"
		, ""
		, "fn_afterQuery"
		, true);
}
/********************************************************************************
* 기      능   : 삽입 20
********************************************************************************/
function fn_insert() {
	// 그리드에 row 추가 
	var newRow = gfn_addGridRow(grd_list, -1, "EXAMREQUEST");
}
/********************************************************************************
* 기      능   : Delete 
********************************************************************************/
function fn_delete() {
	for(var i=0;i<ds_list.GetRowCount();i++){
		if(ds_list.GetColumn(i,"EXAMREQUEST")=="1"){
			if(ds_list.GetColumn(i,"SV_STUS")=="3" || ds_list.GetColumn(i,"SV_STUS")=="4" || ds_list.GetColumn(i,"SV_STUS")=="5"){
				//alert("수정 혹은 삭제가 불가합니다.");
				gfn_showAlert("CW00114","수정 혹은 삭제가");
				ds_list.FireEvent=false;
				ds_list.SetColumn(i,"EXAMREQUEST","0");	
				ds_list.FireEvent=true;			
				return false;
			}
		}	
	}	

	tit_clearActionInfo();
	tit_addSaveActionInfo(
		""
		, "qm05:QM0501002A_D1"
		, ""
		);			

	// 저장 처리 
	tit_callService(
        ""
        , ""
        , "ds_list=ds_list:A"
        , ""
        , ""
        , "fn_afterSave");
}
/********************************************************************************
* 기      능   : 저장 
********************************************************************************/
function fn_save() {
	for(var i=0;i<ds_list.GetRowCount();i++){
		if(ds_list.GetColumn(i,"PARQDT") == ds_list.GetOrgColumn(i,"PARQDT"))
		{
			ds_list.SetColumn(i,"EXAMREQUEST","0");
		}
		if(ds_list.GetColumn(i,"EXAMREQUEST")=="1"){
			/*
			if(ds_list.GetColumn(i,"EXAMTYPE")==""){
				//alert("검사유형을 선택하십시오.");
				gfn_showAlert("CW00001","검사유형");
				return false;
			}
			*/
			if(Length(Trim(ds_list.GetColumn(i,"PARQDT")))!=8){
				//alert("검사요청일을 입력하십시오.");
				gfn_showAlert("CW00001","검사요청일");
				return false;
			}
		}		
	}	

	tit_clearActionInfo();
	tit_addSaveActionInfo(
		""
		, "qm05:QM0501002A_U1"
		, ""
		);	

	// 저장 처리 
	tit_callService(
        ""
        , ""
        , "ds_list=ds_list:A"
        , ""
        , ""
        , "fn_afterSave");
}
