/********************************************************************************
* 기      능   : 사용자 정의 함수  
********************************************************************************/
function ufn_checkModify(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex) {
	ds_checkCond.SetColumn(0,"ART",ds_list.GetColumn(nRow,"ART"));

	tit_clearActionInfo();
	tit_addSearchActionInfo("qm05:QM0501002A_S2");
	tit_callService(
        ""
        , ""
        , "ds_checkCond=ds_checkCond"
        , "ds_outCheck=ds_outCheck"
        , ""
        , "fn_afterQueryC"
        , true);	
}

function ufn_popup(obj, nRow, nCell, nX, nY){	
	if(nCell!=8 && nCell!=9){
		var arg = "prueflos="+quote(ds_list.GetColumn(ds_list.currow,"PRUEFLOS"));
		gfn_openPopup("QM0501002B",true,arg);
	}
}
