/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
function fn_query() 
{
	if ( !gfn_checkFromToObj(redat_from, redat_to, true, Static4.value, 0, true) ) 
	{
		return;
	}

	var sChkRslt = "";	

	if(chk_1.Value == '1' && chk_2.Value == '1') // 자체검사(B), 면제(D) + 건축보류(P), 지도(G) 모두 제외
	{
		sChkRslt = "A";
	}	
	else if(chk_1.Value == '1' && chk_2.Value == '0')	// 자체검사(B), 면제(D) 제외
	{
		sChkRslt = "B";
	}
	else if(chk_1.Value == '0' && chk_2.Value == '1')	// 건축보류(P), 지도(G) 제외
	{
		sChkRslt = "C";
	}
	else	// 전체 포함
	{
		sChkRslt = "D";
	}

	ds_cond.SetColumn(0, "CHK_RSLT", sChkRslt);

    tit_clearActionInfo();
	tit_addSearchActionInfo("qm05:QM0503003A_S1");
	tit_addSearchActionInfo("qm05:QM0503003A_S2");
	tit_addSearchActionInfo("qm05:QM0503003A_S3");
	tit_addSearchActionInfo("qm05:QM0503003A_S4");
	tit_addSearchActionInfo("qm05:QM0503003A_S5");
	tit_addSearchActionInfo("qm05:QM0503003A_S7");
	tit_callService(
		""
		, ""
		, "ds_cond=ds_cond"
		, "ds_list_1=ds_list_1 ds_list_2=ds_list_2 ds_list_3=ds_list_3 ds_list_4=ds_list_4 ds_list_5=ds_list_5 ds_list_7=ds_list_7"  
		, ""
		, "fn_afterQuery"
		, ""
		, ""
		, true);


}



/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) 
{
	// 1. 중간검사 진행현황 -> 합격률
	if(ds_list_1.GetRowCount() > 0)
	{
		var nDaesu_1   = ds_list_1.GetColumn(0, "DAESU_1");
		var nDaesu_2   = ds_list_1.GetColumn(0, "DAESU_2");
		var nPass_1    = ds_list_1.GetColumn(0, "PASS_1");
		var nPass_2    = ds_list_1.GetColumn(0, "PASS_2");
		var nDaesu_All = ds_list_1.GetColumn(0, "DAESU_ALL");
		var nPass_All  = ds_list_1.GetColumn(0, "PASS_ALL");
		var nPer_1 = 0;
		var nPer_2 = 0;
		var nPer_All = 0;

		if(nDaesu_1 == 0)
		{
			nPer_1 = 0;
		}
		else
		{
			nPer_1 = (toNumber(nPass_1) / toNumber(nDaesu_1)) * 100;
		}

		if(nDaesu_2 == 0)
		{
			nPer_2 = 0;
		}
		else
		{
			nPer_2 = (toNumber(nPass_2) / toNumber(nDaesu_2)) * 100;
		}

		if(nDaesu_All == 0)
		{
			nPer_All = 0;
		}
		else
		{
			nPer_All = (toNumber(nPass_All) / toNumber(nDaesu_All)) * 100;
		}


		ds_list_1.SetColumn(0, "PER_1", toString(Round(nPer_1,1))+'%');
		ds_list_1.SetColumn(0, "PER_2", toString(Round(nPer_2,1))+'%');
		ds_list_1.SetColumn(0, "PER_ALL", toString(Round(nPer_All,1))+'%');

	}

	// 2. 완성검사 진행 현황 -> 합격률
	if(ds_list_2.GetRowCount() > 0)
	{
		var nDaesu_1   = ds_list_2.GetColumn(0, "DAESU_1");
		var nDaesu_2   = ds_list_2.GetColumn(0, "DAESU_2");
		var nPass_1    = ds_list_2.GetColumn(0, "PASS_1");
		var nPass_2    = ds_list_2.GetColumn(0, "PASS_2");
		var nDaesu_All = ds_list_2.GetColumn(0, "DAESU_ALL");
		var nPass_All  = ds_list_2.GetColumn(0, "PASS_ALL");

		var nPer_1 = 0;
		var nPer_2 = 0;
		var nPer_All = 0;

		if(nDaesu_1 == 0)
		{
			nPer_1 = 0;
		}
		else
		{
			nPer_1 = (toNumber(nPass_1) / toNumber(nDaesu_1)) * 100;
		}

		if(nDaesu_2 == 0)
		{
			nPer_2 = 0;
		}
		else
		{
			nPer_2  = (toNumber(nPass_2) / toNumber(nDaesu_2)) * 100;
		}

		if(nDaesu_All == 0)
		{
			nPer_All = 0;
		}
		else
		{
			nPer_All = (toNumber(nPass_All) / toNumber(nDaesu_All)) * 100;
		}


		ds_list_2.SetColumn(0, "PER_1", toString(Round(nPer_1,1))+'%');
		ds_list_2.SetColumn(0, "PER_2", toString(Round(nPer_2,1))+'%');
		ds_list_2.SetColumn(0, "PER_ALL", toString(Round(nPer_All,1))+'%');
	}

	// 3. 이체문제점 발생 현황 -> 발생률
	if(ds_list_3.GetRowCount() > 0)	// 
	{
		// 발생률 = 설치판정 / 인계대수 * 100
		var nCnt2 = ds_list_3.GetColumn(0, "CNT_2");	// 설치판정 대수
		var nCnt0 = ds_list_3.GetColumn(0, "CNT_0");	// 인계 대수 

		var nOccurCnt = 0;

		if(nCnt0 == 0)
		{
			nOccurCnt = 0;
		}
		else
		{
			nOccurCnt = (toNumber(nCnt2) / toNumber(nCnt0)) * 100;
		}

		ds_list_3.SetColumn(0, "CNT_3", toString(Round(nOccurCnt,1))+'%');
	}

	// 4. 점수 -> 평균점수
	if(ds_list_4.GetRowCount() > 0)	// 
	{
		var nAvgScore = 0;
		var nScore = ds_list_4.GetColumn(0, "SCORE");
		var nCnt   = ds_list_4.GetColumn(0, "CNT");

		if(nCnt == 0)
		{
			nAvgScore = 0;
		}
		else
		{
			nAvgScore = (toNumber(nScore) / toNumber(nCnt));
		}


		ds_list_4.SetColumn(0, "AVG_SCORE", toString(Round(nAvgScore,1)));
	}

	// 메시지 처리 
	gfn_showSearchMsg(grd_list);

}



