	tit_callService(
		""
		, ""
		, "ds_cond_cd=ds_cond"
		, "ds_tmcd=ds_temno"
		, ""
		, "fn_afterTemno"
		, true);
}

function cb_zzlifnr_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fn_tmcd(); // 팀장 조회
}

function fn_afterTemno()
{
	if(userGroup == '10')
	{
		ds_tmcd.Filter("LIFNR == '"+G_VEND_CODE_E+"'");
	}

	gfn_insertEmptyRow( ds_tmcd, G_CODE_ALL );
	cb_tmcd.Index = 0;
}





