	    arg += " LIFNR="+quote(ds_cond.getColumn(0, "LIFNR"));
	    arg += " CODE="+quote(ds_cond.getColumn(0, "CODE"));
	    arg += " STATUS="+quote(ds_cond.getColumn(0, "STATUS"));
	    arg += " FR_DATE="+quote(ds_cond.getColumn(0, "FR_DATE"));
	    arg += " TO_DATE="+quote(ds_cond.getColumn(0, "TO_DATE"));
	    arg += " TMCD="+quote(ds_cond.getColumn(0, "TMCD"));
	    arg += " EL_TYPE="+quote(ds_cond.getColumn(0, "EL_TYPE"));

	gfn_openPopup("QM0503003B",true,arg);
}

function Tab00_OnChanged(obj,nOldIndex,nNewindex)
{
	//Trace(nNewindex);
	if(nNewindex == 0)	// 완성검사
	{
		Static35.Text = "5. 주요지적사항(10개소)";
	}
	else	// 중간검사
	{
		Static35.Text = "6. 중간검사 지적사항";
	}
}

