/********************************************************************************
* 기      능   : 사무소 변경 시 
********************************************************************************/
function cb_actss_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if(strCode == '') ds_lifnr.Filter("1==1");
	else ds_lifnr.Filter("ZZACTSS=="+quote(strCode)+" || CODE==''");
	cb_zzlifnr.Index = 0;
}
