function fn_insert() {
	// 그리드에 row 추가 
	var newRow = gfn_addGridRow(grd_list, -1, "FLAG");
}
/********************************************************************************
* 기      능   : Delete 
********************************************************************************/
function fn_delete() {
	if ( ds_list.rowCount() < 1 ) {
		// 삭제할 정보 없음. 	
		return;
	}

	// Grid 삭제 처리 
	gfn_deleteGridRow( grd_list, ds_list.row, "FLAG" );
}
/********************************************************************************
* 기      능   : 저장 
********************************************************************************/
function fn_save() {

	if(!fn_checkSave())
		return;


	tit_clearActionInfo();
	tit_addSaveActionInfo(
		"qm05:QM0505001A_I1"
		, ""
		, ""
		, "FLAG");

	tit_callService(
		""
		, ""
		, "ds_list=ds_list:A"
		, ""
		, ""
		, "fn_afterSave"
		,""
		,""
		,"true");

}

/********************************************************************************
* 기      능   : 저장전에 자료 검증 
********************************************************************************/
function fn_checkSave(){
	var j = 0;
	var nAddRow = 0;
    ds_list_d2.ClearData();
    ds_list_d.ClearData();


	for(var i=0;i<ds_list.GetRowCount();i++){
		ds_exam.ClearData();

		if(ds_list.GetColumn(i,"CHK")=="1"){
			   j=j+1;
			   ds_exam.AddRow();
               ds_exam.SetColumn(0,"ZZACTSS", '88');
               ds_exam.SetColumn(0,"EXAMREQDATE",ds_list.GetColumn(i,"PARQDT"));

                tit_clearActionInfo();
		        tit_addSearchActionInfo("qm05:QM0501001A_S11");
		        tit_callService(
			     ""
			   , ""
			   , "ds_exam=ds_exam"
			   , "ds_list_d=ds_list_d"
			   , ""
			   , ""//"ufn_afterdaesuCount"
			   , ""
               , ""
			   , true);	


			   var sMdate = ds_list_d.GetColumn(0, "MDATE");
			   var sZzactss = ds_list_d.GetColumn(0, "ZZACTSS");
			   var nSum = ds_list.CaseSum("CHK == '1' && ZZACTSS == '"+sZzactss+"' && PARQDT == '"+sMdate+"'", "DAESU");

			   if(ds_list_d.GetColumn(0,"DAESU_T") < toNumber(ds_list_d.GetColumn(0,"DAESU_H")) + toNumber(nSum))
			   {
					var txt = (parseInt(i) + 1) + "행 검사요청일 신청대수가 초과하였습니다.다른 일자를";
  	                gfn_showAlert("CW00116", txt);
		            return false;
			   }
			   else
			   {
					nAddRow = ds_list_d2.AddRow();
					ds_list_d2.copyRow(nAddRow, ds_list_d, 0);
					ds_list_d2.SetColumn(nAddRow,"DAESU_H2", ds_list_d.GetColumn(0,"DAESU_H") + nSum);
			   }




      //    trace(ds_list_d.SaveXML());
      //     trace(ds_list_d2.SaveXML());

		}

	//	trace(ds_list_d.SaveXML());
	}

	if(j==0){
		//alert("검사신청을 하나이상 체크하십시오.");
		gfn_showAlert("CW00013");
		return false;
	}

	return true;
}
