/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave(errCode, errMsg) {
	if ( errCode != 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");

		tit_clearActionInfo();
        tit_addMultiActionInfo("qm05:QM0501001A_U3");
        tit_callService(
			  ""	
			, ""
			, "ds_list_d2=ds_list_d2"
			, ""
			, ""
			, ""//"fn_afterSave"
			, ""
			, ""
			, true  // ""
		);
		// 저장 성공시 재조회
		fn_query();
	}
}
