function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 0 ) {
		// 그리드 표시할 컬럼 선택 
		gfn_selectGridCol(obj);
	} else {
		// 그리드 정렬 처리 
		gfn_sortGrid(obj,nCell);
	}
}
/********************************************************************************
* 기      능   : 데이터셋 값 변경 시 - Flag 처리 
********************************************************************************/
function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	ds_list.FireEvent=false;

	switch( strColumnID ) {
	    case "PARQDT":
			if(trim(ds_list.GetColumn(nRow,"PARQDT")) > "")
			{
				ds_list.SetColumn(nRow,"CHK","1");
				ds_list.SetColumn(nRow,"FLAG","I");
				ufn_chkdate();

			}
			else 
			{
				ds_list.SetColumn(nRow,"CHK","");
				ds_list.SetColumn(nRow,"FLAG","");
			}
	        break;
	}

	if(strColumnId == "INTE_LFNM") // 작업업체 변경 시, 작업업체명 변경
	{


		if(length(Trim(varNewValue)) == 0)
		{
			ds_list.SetColumn(nRow, "INTE_LFNR", "");
			ds_list.SetColumn(nRow, "INTE_LFNM", "");
			ds_list.SetColumn(nRow, "INTE_NAME", "");
			ds_list.SetColumn(nRow, "INTE_CELL", "");
			return;
		}

		// 존재하는 사업자번호 체크 (미존재시 팝업)
		nFindRow = ufn_chkLifnr(varNewValue);
		Trace("nFindRow : "+nFindRow);
		if(nFindRow <= 0)	// 사업자 번호 미존재시 팝업을 띄워준다.
		{
			var arg = "";
			var arr = gfn_openPopup("QM0505001B", true, arg);

			if(Length(arr) > 0)
			{
				ds_list.SetColumn(nRow, "INTE_LFNR", arr[0]);
				ds_list.SetColumn(nRow, "INTE_LFNM", arr[1]);
				ds_list.SetColumn(nRow, "INTE_NAME", arr[2]);
				ds_list.SetColumn(nRow, "INTE_CELL", arr[3]);
			}

		}
		else
		{
			if(nFindRow == 1)
			{
				ds_list.SetColumn(nRow, "INTE_LFNR", ds_lifnr_grid.GetColumn(0, "CODE"));
				ds_list.SetColumn(nRow, "INTE_LFNM", ds_lifnr_grid.GetColumn(0, "CODE_NAME"));
				ds_list.SetColumn(nRow, "INTE_NAME", ds_lifnr_grid.GetColumn(0, "NAME"));
				ds_list.SetColumn(nRow, "INTE_CELL", ds_lifnr_grid.GetColumn(0, "CELLP"));
			}
			else
			{
				var arg = "F_LIFNR_N="+quote(varNewValue);
				var arr = gfn_openPopup("QM0505001B", true, arg);

				if(Length(arr) > 0)
				{
					ds_list.SetColumn(nRow, "INTE_LFNR", arr[0]);
					ds_list.SetColumn(nRow, "INTE_LFNM", arr[1]);
					ds_list.SetColumn(nRow, "INTE_NAME", arr[2]);
					ds_list.SetColumn(nRow, "INTE_CELL", arr[3]);
				}
			}

		}



	}
	ds_list.FireEvent=true;
}

function ufn_chkdate(){

	if(ds_exam.GetRowCount()==0)	
		ds_exam.AddRow();

      ds_exam.SetColumn(0,"EXAMREQDATE",ds_list.GetColumn(ds_list.currow,"PARQDT"));
      ds_exam.SetColumn(0,"ZZACTSS", '88'); 


	tit_clearActionInfo();
	tit_addSearchActionInfo("qm05:QM0501001A_S12");
	tit_callService(
		""
		, ""
		, "ds_exam=ds_exam"
		, "ds_eCount=ds_eCount"
		, ""
		, "ufn_afterexamCount"
		, true);	

}

function ufn_afterexamCount(){ //alert(ds_eCount.GetColumn(0,"ZDATE"));
	if(length(ds_eCount.GetColumn(0,"ZDATE")) > 0){
	   ufn_chktime();
	}
	else{		
		   gfn_showAlert("검사 스케줄이 입력되지 않았습니다. 담당자에게 문의하세요.");
		   ds_list.SetColumn(ds_list.currow, "CHK", '0');
		   ds_list.SetColumn(ds_list.currow, "FLAG", '');
		   ds_list.SetColumn(ds_list.currow, "PARQDT", '');
		   return false;
	}
}

function ufn_chktime(){
      if(ToNumber(ds_eCount.GetColumn(0,"ZDATE")) < ToNumber(getDate())){
		   gfn_showAlert("검사 신청이 마감되었습니다.");
		   ds_list.SetColumn(ds_list.currow, "CHK", '0');
		   ds_list.SetColumn(ds_list.currow, "FLAG", '');
		   ds_list.SetColumn(ds_list.currow, "PARQDT", '');
		   return false;
		}	
}
// 달력 처리 부분 
/********************************************************************************
* 기      능   : 그리드에서 날짜 입력시 
********************************************************************************/
function grd_list_OnExpandEdit(obj,nRow,nCell,strVal,nPivotIndex)
{
	var rect = obj.GetCellRect(nRow, ncell);

	if(nCell == 7)	// 검사요청일
	{
		cal_temp.top = rect[1] + 80;
		if (cal_temp.top > 400)
		{
			cal_temp.top = 400;
		}
		cal_temp.Left = rect[0] + 20;
		if (cal_temp.Left > 790)
		{
			cal_temp.Left = 790;
		}	 
		cal_temp.Visible = true;

		cal_temp.value = ds_list.getColumn(nRow, "PARQDT");

		cal_temp.SetFocus();
	}
	else if(nCell == 8)	// 업체명
	{
		//var arg = ds_list.GetColumn(nRow, "WRKLFN");
		var arg = "";
		var arr = gfn_openPopup("QM0505001B", true, arg);

		if(Length(arr) > 0)
		{
			//ds_list.fireevent = false;
			ds_list.SetColumn(nRow, "INTE_LFNR", arr[0]);
			ds_list.SetColumn(nRow, "INTE_LFNM", arr[1]);
			ds_list.SetColumn(nRow, "INTE_NAME", arr[2]);
			ds_list.SetColumn(nRow, "INTE_CELL", arr[3]);
			//ds_list.fireevent = true;
		}
	}


}
