function ufn_chkLifnr(lifnr)
{
	var rValue = 0;

	ds_condLifnr.ClearData();
	ds_condLifnr.addRow();
	ds_condLifnr.SetColumn(0, "LIFNR_N", lifnr);

	tit_clearActionInfo();
	tit_addSearchActionInfo("qm05:QM0505001B_S1");
	tit_callService(
		""
		, ""
		, "ds_cond=ds_condLifnr"
		, "ds_lifnr_grid=ds_list"
		, ""
		, "" // fn_afterQueryC
		, ""
		, ""
		, true);

	//Trace(ds_lifnr_grid.saveXML());
	if(ds_lifnr_grid.GetRowCount() == 0)
	{
		rValue = -1;
	}
	else
	{
		rValue = ds_lifnr_grid.GetRowCount();
	}

	return rValue;
}
