/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
function fn_query() {
	// 검사요청일
	if ( !gfn_checkFromToObj(cal_parqdt_f, cal_parqdt_t, true, st_parqdt.value, 0, true) ) {
		return;
	}

	if(ed_pjt.value != "")
		ds_cond.SetColumn(0,"GBN","A");
	else if(ed_pjt.value == "")
		ds_cond.SetColumn(0,"GBN","B");

	var strCheck="0000";	
	if(ds_cond.GetColumn(0,"CHECK1")=="1"){
		strCheck = "1"+SubStr(strCheck,1,3);
	}
	if(ds_cond.GetColumn(0,"CHECK2")=="1"){
		strCheck = SubStr(strCheck,0,1)+"1"+SubStr(strCheck,2,2);
	}
	if(ds_cond.GetColumn(0,"CHECK3")=="1"){
		strCheck = SubStr(strCheck,0,2)+"1"+SubStr(strCheck,3,1);
	}	
	if(ds_cond.GetColumn(0,"CHECK4")=="1"){
		strCheck = SubStr(strCheck,0,3)+"1";
	}				
	ds_cond.SetColumn(0,"CHECK",strCheck);

	if(user_grp == "10") ds_cond.SetColumn(0,"GUBUN","A");
	else ds_cond.SetColumn(0,"GUBUN","B");

	tit_clearActionInfo();
	tit_addSearchActionInfo("qm05:QM0505002A_S1");

	tit_callService(
		""
		, ""
		, "ds_cond=ds_cond"
		, "ds_list=ds_list"
		, ""
		, "fn_afterQuery"
		, true);
}
