function fn_insert() {
	// 그리드에 row 추가 
	var newRow = gfn_addGridRow(grd_list, -1, "EXAMREQUEST");
}
/********************************************************************************
* 기      능   : Delete 
********************************************************************************/
function fn_delete() {
	var j = 0;
	var nAddRow = 0;
    ds_list_d2.ClearData();
    ds_list_d1.ClearData();
    ds_list_d.ClearData();

	for(var i=0;i<ds_list.GetRowCount();i++){
		if(ds_list.GetColumn(i,"EXAMREQUEST")=="1"){
			if(ds_list.GetColumn(i,"SV_STUS")=="3" || ds_list.GetColumn(i,"SV_STUS")=="4" || ds_list.GetColumn(i,"SV_STUS")=="5"){
				//alert("수정 혹은 삭제가 불가합니다.");
				gfn_showAlert("CW00114","수정 혹은 삭제가");
				ds_list.FireEvent=false;
				ds_list.SetColumn(i,"EXAMREQUEST","0");	
				ds_list.FireEvent=true;			
				return false;
			}

			   ds_exam.AddRow();
               ds_exam.SetColumn(0,"ZZACTSS", '88');
               ds_exam.SetColumn(0,"EXAMREQDATE",ds_list.GetColumn(i,"PARQDT"));

                tit_clearActionInfo();
		        tit_addSearchActionInfo("qm05:QM0501001A_S11");
		        tit_callService(
			     ""
			   , ""
			   , "ds_exam=ds_exam"
			   , "ds_list_d=ds_list_d"
			   , ""
			   , ""//"ufn_afterdaesuCount"
			   , ""
               , ""
			   , true);	

			   var sMdate = ds_list_d.GetColumn(0, "MDATE");
			   var sZzactss = ds_list_d.GetColumn(0, "ZZACTSS");
			   var nSum = ds_list.CaseSum("EXAMREQUEST == '1' && ZZACTSS2 == '"+sZzactss+"' && PARQDT == '"+sMdate+"'", "DAESU");


			   if(ds_list_d.GetRowCount() > 0)
			   {
					nAddRow = ds_list_d2.AddRow();
					ds_list_d2.copyRow(nAddRow, ds_list_d, 0);
					ds_list_d2.SetColumn(nAddRow,"DAESU_H", toNumber(ds_list_d.GetColumn(0,"DAESU_H")) - toNumber(nSum));
			   }

			Trace(ds_list_d2.saveXML());


		}	
	}	

	tit_clearActionInfo();
	tit_addSaveActionInfo(
		""
		, "qm05:QM0501002A_D1"
		, ""
		);			

	// 저장 처리 
	tit_callService(
        ""
        , ""
        , "ds_list=ds_list:A"
        , ""
        , ""
        , "fn_afterDel");
}
/********************************************************************************
* 기      능   : 저장 
********************************************************************************/
function fn_save() {
	var j = 0;
	var nAddRow = 0;
    ds_list_d2.ClearData();
    ds_list_d.ClearData();

	for(var i=0;i<ds_list.GetRowCount();i++){
		ds_exam.ClearData();

		if(ds_list.GetColumn(i,"PARQDT") == ds_list.GetOrgColumn(i,"PARQDT"))
		{
			ds_list.SetColumn(i,"EXAMREQUEST","0");
		}
		if(ds_list.GetColumn(i,"EXAMREQUEST")=="1"){
			/*
			if(ds_list.GetColumn(i,"EXAMTYPE")==""){
				//alert("검사유형을 선택하십시오.");
				gfn_showAlert("CW00001","검사유형");
				return false;
			}
			*/
			if(Length(Trim(ds_list.GetColumn(i,"PARQDT")))!=8){
				//alert("검사요청일을 입력하십시오.");
				gfn_showAlert("CW00001","검사요청일");
				return false;
			}
		}

		// 검사요청일이 변경되었을 경우에만 다음 로직을 수행.
		if(ds_list.GetColumn(i,"PARQDT") != ds_list.GetOrgColumn(i,"PARQDT") && ds_list.GetColumn(i,"EXAMREQUEST") == "1")
		{
			   j=j+1;
			   ds_exam.AddRow();
               ds_exam.SetColumn(0,"ZZACTSS", '88');
               ds_exam.SetColumn(0,"EXAMREQDATE",ds_list.GetColumn(i,"PARQDT"));

                tit_clearActionInfo();
		        tit_addSearchActionInfo("qm05:QM0501001A_S11");
		        tit_callService(
			     ""
			   , ""
			   , "ds_exam=ds_exam"
			   , "ds_list_d=ds_list_d"
			   , ""
			   , ""//"ufn_afterdaesuCount"
			   , ""
               , ""
			   , true);	

			   var sMdate = ds_list_d.GetColumn(0, "MDATE");
			   var sZzactss = ds_list_d.GetColumn(0, "ZZACTSS");
			   var nSum = ds_list.CaseSum("EXAMREQUEST == '1' && ZZACTSS2 == '"+sZzactss+"' && PARQDT == '"+sMdate+"'", "DAESU");
			   //Trace("mdate : "+ sMdate);
			   //Trace("sum : "+nSum);
			   if(ds_list_d.GetColumn(0,"DAESU_T") < toNumber(ds_list_d.GetColumn(0,"DAESU_H")) + toNumber(nSum))
			   {
					var txt = (parseInt(i) + 1) + "행 검사요청일 신청대수가 초과하였습니다.다른 일자를";
  	                gfn_showAlert("CW00116", txt);
		            return false;
			   }
			   else
			   {
					nAddRow = ds_list_d2.AddRow();
					ds_list_d2.copyRow(nAddRow, ds_list_d, 0);
					ds_list_d2.SetColumn(nAddRow,"DAESU_H2", ds_list_d.GetColumn(0,"DAESU_H") + nSum);
			   }
		}
	}	
	//Trace(ds_list.saveXML());
	//Trace(ds_list_d2.saveXML());

	tit_clearActionInfo();
	tit_addSaveActionInfo(
		""
		, "qm05:QM0501002A_U1"
		, ""
		);	

	// 저장 처리 
	tit_callService(
        ""
        , ""
        , "ds_list=ds_list:A"
        , ""
        , ""
        , "fn_afterSave");
}
