        tit_addMultiActionInfo("qm05:QM0501002A_U2");
        tit_callService(
		  ""	
		, ""
		, "ds_list_d2=ds_list_d2"
		, ""
		, ""
		, ""//"fn_afterSave"
		, ""
		, ""
		, true  // ""
    );
    }

    fn_query();
 }

