function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 0 ) {
		// 그리드 표시할 컬럼 선택 
		gfn_selectGridCol(obj);
	} else {
		// 그리드 정렬 처리 
		gfn_sortGrid(obj,nCell);
	}
}
/********************************************************************************
* 기      능   : 데이터셋 값 변경 시 - EXAMREQUEST 처리 
********************************************************************************/
function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID=="PARQDT"){	
		if(ToNumber(varNewValue) < ToNumber(gfn_getCurrDate()) || ToNumber(varNewValue) < ToNumber(ds_list.GetColumn(nRow,"PACFDT"))){
			//alert("검사요청일을 확인하십시오.");
			gfn_showAlert("CW00022","검사요청일");
			ds_list.FireEvent=false;
			ds_list.SetColumn(nRow,strColumnID,ds_list.GetOrgColumn(nRow, "PARQDT"));
			ds_list.SetColumn(nRow,"EXAMREQUEST","0");			
			ds_list.FireEvent=true;

			return;
		}
		else if(ds_list.GetColumn(nRow,"SV_STUS")=="4" || ds_list.GetColumn(nRow,"SV_STUS")=="5"){			
			//alert("검사요청일을 수정하실수 없습니다.");
			gfn_showAlert("CW00022","검사요청일");
			ds_list.FireEvent=false;
			ds_list.SetColumn(nRow,strColumnID,ds_list.GetOrgColumn(nRow, "PARQDT"));
			ds_list.SetColumn(nRow,"EXAMREQUEST","0");	
			ds_list.FireEvent=true;	

			return;
		}
		else if(ds_list.GetColumn(nRow,"SV_STUS")=="3"){
		 //체크
			ufn_checkModify(obj,nRow,strColumnID,ds_list.GetOrgColumn(nRow, "PARQDT"),varNewValue,nPivotIndex);

		}
		else{
			ds_list.FireEvent=false;
			ds_list.SetColumn(nRow,"EXAMREQUEST","1");
			ds_list.FireEvent=true;			
		}

		ufn_chkdate();
	}	
	else if(strColumnID=="EXAMREQUEST"){	
		if(ds_list.GetColumn(nRow,"SV_STUS")=="3" || ds_list.GetColumn(nRow,"SV_STUS")=="4" || ds_list.GetColumn(nRow,"SV_STUS")=="5"){
			//alert("수정 혹은 삭제가 불가합니다.");
			gfn_showAlert("CW00114","수정 혹은 삭제가");
			ds_list.FireEvent=false;
			ds_list.SetColumn(nRow,"EXAMREQUEST","0");	
			ds_list.FireEvent=true;			
		}
	}
	else if(strColumnId == "INTE_LFNM") // 작업업체 변경 시, 작업업체명 변경
	{


		if(length(Trim(varNewValue)) == 0)
		{
			ds_list.SetColumn(nRow, "INTE_LFNR", "");
			ds_list.SetColumn(nRow, "INTE_LFNM", "");
			ds_list.SetColumn(nRow, "INTE_NAME", "");
			ds_list.SetColumn(nRow, "INTE_CELL", "");
			return;
		}

		// 존재하는 사업자번호 체크 (미존재시 팝업)
		nFindRow = ufn_chkLifnr(varNewValue);
		Trace("nFindRow : "+nFindRow);
		if(nFindRow <= 0)	// 사업자 번호 미존재시 팝업을 띄워준다.
		{
			var arg = "";
			var arr = gfn_openPopup("QM0505001B", true, arg);

			if(Length(arr) > 0)
			{
				ds_list.SetColumn(nRow, "INTE_LFNR", arr[0]);
				ds_list.SetColumn(nRow, "INTE_LFNM", arr[1]);
				ds_list.SetColumn(nRow, "INTE_NAME", arr[2]);
				ds_list.SetColumn(nRow, "INTE_CELL", arr[3]);
			}

		}
		else
		{
			if(nFindRow == 1)
			{
				ds_list.SetColumn(nRow, "INTE_LFNR", ds_lifnr_grid.GetColumn(0, "CODE"));
				ds_list.SetColumn(nRow, "INTE_LFNM", ds_lifnr_grid.GetColumn(0, "CODE_NAME"));
				ds_list.SetColumn(nRow, "INTE_NAME", ds_lifnr_grid.GetColumn(0, "NAME"));
				ds_list.SetColumn(nRow, "INTE_CELL", ds_lifnr_grid.GetColumn(0, "CELLP"));
			}
			else
			{
				var arg = "F_LIFNR_N="+quote(varNewValue);
				var arr = gfn_openPopup("QM0505001B", true, arg);

				if(Length(arr) > 0)
				{
					ds_list.SetColumn(nRow, "INTE_LFNR", arr[0]);
					ds_list.SetColumn(nRow, "INTE_LFNM", arr[1]);
					ds_list.SetColumn(nRow, "INTE_NAME", arr[2]);
					ds_list.SetColumn(nRow, "INTE_CELL", arr[3]);
				}
			}

		}



	}
}
