/********************************************************************************
* 기      능   : 그리드에서 날짜 입력시 
********************************************************************************/
function grd_list_OnExpandEdit(obj,nRow,nCell,strVal,nPivotIndex)
{
	if(nCell == 9)
	{
		var rect = obj.GetCellRect(nRow, ncell);

		cal_temp.top = rect[1] + 80;
		if (cal_temp.top > 400)
		{
			cal_temp.top = 400;
		}
		cal_temp.Left = rect[0] + 20; 
		if (cal_temp.Left > 790)
		{
			cal_temp.Left = 790;
		}	 
		cal_temp.Visible = true;
		cal_temp.value = ds_list.getColumn(nRow, "PARQDT");
		cal_temp.SetFocus();
	}
	else if(nCell == 10)	// 업체명
	{
		//var arg = ds_list.GetColumn(nRow, "WRKLFN");
		var arg = "";
		var arr = gfn_openPopup("QM0505001B", true, arg);

		if(Length(arr) > 0)
		{
			//ds_list.fireevent = false;
			ds_list.SetColumn(nRow, "INTE_LFNR", arr[0]);
			ds_list.SetColumn(nRow, "INTE_LFNM", arr[1]);
			ds_list.SetColumn(nRow, "INTE_NAME", arr[2]);
			ds_list.SetColumn(nRow, "INTE_CELL", arr[3]);
			//ds_list.fireevent = true;
		}
	}


}
