function ufn_chkLifnr(lifnr)
{
	var rValue = 0;

	ds_condLifnr.ClearData();
	ds_condLifnr.addRow();
	ds_condLifnr.SetColumn(0, "LIFNR_N", lifnr);

	tit_clearActionInfo();
	tit_addSearchActionInfo("qm05:QM0505001B_S1");
	tit_callService(
		""
		, ""
		, "ds_cond=ds_condLifnr"
		, "ds_lifnr_grid=ds_list"
		, ""
		, "" // fn_afterQueryC
		, ""
		, ""
		, true);


	if(ds_lifnr_grid.GetRowCount() == 0)
	{
		rValue = -1;
	}
	else
	{
		rValue = ds_lifnr_grid.GetRowCount();
	}

	return rValue;
}

function ufn_chkdate(){

	if(ds_exam.GetRowCount()==0)	
		ds_exam.AddRow();

      ds_exam.SetColumn(0,"EXAMREQDATE",ds_list.GetColumn(ds_list.currow,"PARQDT"));
      ds_exam.SetColumn(0,"ZZACTSS", '88'); 


	tit_clearActionInfo();
	tit_addSearchActionInfo("qm05:QM0501001A_S12");
	tit_callService(
		""
		, ""
		, "ds_exam=ds_exam"
		, "ds_eCount=ds_eCount"
		, ""
		, "ufn_afterexamCount"
		, true);	

}

function ufn_afterexamCount(){ //alert(ds_eCount.GetColumn(0,"ZDATE"));
	if(length(ds_eCount.GetColumn(0,"ZDATE")) > 0){
	   ufn_chktime();
	}
	else{		
		   gfn_showAlert("검사 스케줄이 입력되지 않았습니다. 담당자에게 문의하세요.");
		   ds_list.FireEvent=false;
		   ds_list.SetColumn(ds_list.currow, "EXAMREQUEST", '0');
		   ds_list.SetColumn(ds_list.currow, "FLAG", '');
		   ds_list.SetColumn(ds_list.currow, "PARQDT", ds_list.GetOrgColumn(ds_list.currow, "PARQDT"));
		   ds_list.FireEvent=true;
		   return false;
	}
}

function ufn_chktime(){
      if(ToNumber(ds_eCount.GetColumn(0,"ZDATE")) < ToNumber(getDate())){
		   gfn_showAlert("검사 신청이 마감되었습니다.");
		   ds_list.FireEvent=false;
		   ds_list.SetColumn(ds_list.currow, "EXAMREQUEST", '0');
		   ds_list.SetColumn(ds_list.currow, "FLAG", '');
		   ds_list.SetColumn(ds_list.currow, "PARQDT", ds_list.GetOrgColumn(ds_list.currow, "PARQDT"));
		   ds_list.FireEvent=true;
		   return false;
		}	
}
