function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);

	var user_grp  = "";
	var user_grp1 = ""; //영업
	var user_grp2 = ""; //설치
	var user_grp3 = ""; //보수
	var user_grp4 = ""; //자재


    user_grp1 = G_USER_GROUP_B; //영업
	user_grp2 = G_USER_GROUP_E; //설치
	user_grp3 = G_USER_GROUP_R; //보수
	user_grp4 = G_USER_GROUP_M; //자재

	user_grp = G_USER_GROUP;

	if(G_VEND_CODE == "0000009922") { //정보기술실
		user_grp = "Z40";
		btn_bannab_in.Enable = true;
		btn_print.Enable = true;
		btn_ton_in.Enable = true;
		btn_date_in.Enable = true;
		btn_register.Enable = true;
	} else if(user_grp1 > "") { //영업
		btn_bannab_in.Enable = false;
		btn_print.Enable = false;
		btn_ton_in.Enable = false;
		btn_date_in.Enable = false;
		btn_register.Enable = false;
		user_grp = user_grp1;
	} else if(user_grp2 > "") { //설치
		btn_bannab_in.Enable = true;
		btn_print.Enable = true;
		btn_ton_in.Enable = false;
		btn_date_in.Enable = true;
		btn_register.Enable = true;
		user_grp = user_grp2;
	} else if(user_grp3 > "") { //보수
		btn_bannab_in.Enable = false;
		btn_print.Enable = false;
		btn_ton_in.Enable = true;
		btn_date_in.Enable = true;
		btn_register.Enable = true;
		user_grp = user_grp3;
	} else if(user_grp4 > "") { //자재
		btn_bannab_in.Enable = false;
		btn_print.Enable = false;
		btn_ton_in.Enable = true;
		btn_date_in.Enable = true;
		btn_register.Enable = true;
		user_grp = user_grp4;
	} else {
		btn_bannab_in.Enable = false;
		btn_print.Enable = false;
		btn_ton_in.Enable = false;
		btn_date_in.Enable = false;
		btn_register.Enable = false;
	}

	// 공급 업체명 설정 
	// ed_lifnrName.value = G_VEND_NAME;
	// ds_cond.setColumn(0, "I_LIFNR", G_VEND_CODE_M);

	//cal_i_credt2.value = gfn_getCurrDate();
	//cal_i_credt1.value = gfn_getafterDate(cal_i_credt2.value, -7);	

	// cal_i_appdt1.value = gfn_getCurrDate();
	// cal_i_appdt2.value = gfn_getAfterDate(cal_i_appdt1.value, 7);	

	// cal_i_sdate1.value = gfn_getCurrDate();
	// cal_i_sdate2.value = gfn_getAfterDate(cal_i_sdate1.value, 7);	

	gfn_showMsg("CI00007");
}


